let handler = async (m, { conn, text}) => {
m.reply(`*❆❯━━━❲Gojo❳━━━━━❮❆*\n➢ *"${pickRandom(global.piropo)}"*\n*❆❯━━━❲Gojo❳━━━━━❮❆*`)
}
handler.tags = ['احاديث']
handler.command = ['حديث','احاديث']
 export default handler

 function pickRandom(list) {
 return list[Math.floor(list.length * Math.random())]}

 global.piropo = [
"لَمَّا خَلَقَ اللَّهُ الخَلْقَ، كَتَبَ في كِتَابِهِ، فَهو عِنْدَهُ فَوْقَ العَرْشِ: إنَّ رَحْمَتي تَغْلِبُ غَضَبِي.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"لَمَّا قَضَى اللَّهُ الخَلْقَ، كَتَبَ في كِتَابِهِ علَى نَفْسِهِ، فَهو مَوْضُوعٌ عِنْدَهُ إنَّ رَحْمَتي تَغْلِبُ غَضَبِي.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"قالَ اللَّهُ عَزَّ وَجَلَّ: سَبَقَتْ رَحْمَتي غَضَبِي.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"صَلَّيْتُ مع أبِي مُوسَى الأشْعَرِيِّ صَلَاةً فَلَمَّا كانَ عِنْدَ القَعْدَةِ قالَ رَجُلٌ مِنَ القَوْمِ: أُقِرَّتِ الصَّلَاةُ بالبِرِّ والزَّكَاةِ؟ قالَ فَلَمَّا قَضَى أبو مُوسَى الصَّلَاةَ وسَلَّمَ انْصَرَفَ فَقالَ: أيُّكُمُ القَائِلُ كَلِمَةَ كَذَا وكَذَا؟ قالَ: فأرَمَّ القَوْمُ، ثُمَّ قالَ: أيُّكُمُ القَائِلُ كَلِمَةَ كَذَا وكَذَا؟ فأرَمَّ القَوْمُ، فَقالَ: لَعَلَّكَ يا حِطَّانُ قُلْتَهَا؟ قالَ: ما قُلتُهَا، ولقَدْ رَهِبْتُ أنْ تَبْكَعَنِي بهَا فَقالَ رَجُلٌ مِنَ القَوْمِ: أنَا قُلتُهَا، ولَمْ أُرِدْ بهَا إلَّا الخَيْرَ فَقالَ أبو مُوسَى: أما تَعْلَمُونَ كيفَ تَقُولونَ في صَلَاتِكُمْ؟ إنَّ رَسولَ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ خَطَبَنَا فَبَيَّنَ لَنَا سُنَّتَنَا وعَلَّمَنَا صَلَاتَنَا. فَقالَ: إذَا صَلَّيْتُمْ فأقِيمُوا صُفُوفَكُمْ ثُمَّ لْيَؤُمَّكُمْ أحَدُكُمْ، فَإِذَا كَبَّرَ فَكَبِّرُوا، وإذْ قالَ {غَيْرِ المَغْضُوبِ عليهم ولَا الضَّالِّينَ} [الفاتحة: 7]، فَقُولوا: آمِينَ، يُجِبْكُمُ اللَّهُ فَإِذَا كَبَّرَ ورَكَعَ فَكَبِّرُوا وارْكَعُوا، فإنَّ الإمَامَ يَرْكَعُ قَبْلَكُمْ، ويَرْفَعُ قَبْلَكُمْ، فَقالَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ: فَتِلْكَ بتِلْكَ وإذَا قالَ: سَمِعَ اللَّهُ لِمَن حَمِدَهُ. فَقُولوا: اللَّهُمَّ رَبَّنَا لكَ الحَمْدُ يَسْمَعُ اللَّهُ لَكُمْ، فإنَّ اللَّهَ تَبَارَكَ وتَعَالَى، قالَ علَى لِسَانِ نَبِيِّهِ صَلَّى اللَّهُ عليه وسلَّمَ: سَمِعَ اللَّهُ لِمَن حَمِدَهُ وإذَا كَبَّرَ وسَجَدَ فَكَبِّرُوا واسْجُدُوا فإنَّ الإمَامَ يَسْجُدُ قَبْلَكُمْ ويَرْفَعُ قَبْلَكُمْ فَقالَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ: فَتِلْكَ بتِلْكَ، وإذَا كانَ عِنْدَ القَعْدَةِ فَلْيَكُنْ مِن أوَّلِ قَوْلِ أحَدِكُمْ: التَّحِيَّاتُ الطَّيِّبَاتُ الصَّلَوَاتُ لِلَّهِ السَّلَامُ عَلَيْكَ أيُّها النبيُّ ورَحْمَةُ اللهِ وبَرَكَاتُهُ، السَّلَامُ عَلَيْنَا وعلَى عِبَادِ اللهِ الصَّالِحِينَ، أشْهَدُ أنْ لا إلَهَ إلَّا اللَّهُ وأَشْهَدُ أنَّ مُحَمَّدًا عَبْدُهُ ورَسولُهُ. وفي رواية: فإنَّ اللَّهَ عزَّ وجلَّ قَضَى علَى لِسَانِ نَبِيِّهِ صَلَّى اللَّهُ عليه وسلَّمَ سَمِعَ اللَّهُ لِمَن حَمِدَهُ.\n\n- الراوي: أبو موسي الاشعري\n- حكم الحديث: [صحيح]",
"أنَّ رَسُولَ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ، سُئِلَ عن أَوْلَادِ المُشْرِكِينَ فَقالَ: اللَّهُ أَعْلَمُ بما كَانُوا عَامِلِينَ.غَيْرَ أنَّ في حَديثِ شُعَيْبٍ وَمَعْقِلٍ: سُئِلَ عن ذَرَارِيِّ المُشْرِكِينَ.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"سُئِلَ رَسُولُ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ، عن أَطْفَالِ المُشْرِكِينَ، مَن يَمُوتُ منهمْ صَغِيرًا؟ فَقالَ: اللَّهُ أَعْلَمُ بما كَانُوا عَامِلِينَ.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"سُئِلَ رَسُولُ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ، عن أَطْفَالِ المُشْرِكِينَ؟ قالَ: اللَّهُ أَعْلَمُ بما كَانُوا عَامِلِينَ إذْ خَلَقَهُمْ.\n\n- الراوي: عبدالله بن عباس \n- حكم الحديث: [صحيح]",
"أنَّ رَسولَ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ كانَ لا يَطْرُقُ أَهْلَهُ لَيْلًا، وَكانَ يَأْتِيهِمْ غُدْوَةً، أَوْ عَشِيَّةً.وفي رواية : بمِثْلِهِ، غيرَ أنَّهُ قالَ: كانَ لا يَدْخُلُ.\n\n- الراوي: انس بن مالك \n- حكم الحديث: [صحيح]",
"إذا أمَّ أحَدُكُمُ النَّاسَ، فَلْيُخَفِّفْ، فإنَّ فِيهِمُ الصَّغِيرَ، والْكَبِيرَ، والضَّعِيفَ، والْمَرِيضَ، فإذا صَلَّى وحْدَهُ فَلْيُصَلِّ كيفَ شاءَ.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"جَاءَ رَجُلٌ إلى رَسولِ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ فَقالَ: إنِّي لأَتَأَخَّرُ عن صَلَاةِ الصُّبْحِ مِن أجْلِ فُلَانٍ، ممَّا يُطِيلُ بنَا فَما رَأَيْتُ النبيَّ صَلَّى اللَّهُ عليه وسلَّمَ غَضِبَ في مَوْعِظَةٍ قَطُّ أشَدَّ ممَّا غَضِبَ يَومَئذٍ فَقالَ: يا أيُّها النَّاسُ إنَّ مِنكُم مُنَفِّرِينَ، فأيُّكُمْ أمَّ النَّاسَ، فَلْيُوجِزْ فإنَّ مِن ورَائِهِ الكَبِيرَ، والضَّعِيفَ وذَا الحَاجَةِ.\n\n- الراوي: أبو مسعود عقبة بن عمرو\n- حكم الحديث: [صحيح]",
"أنَّ النبيَّ صَلَّى اللَّهُ عليه وسلَّمَ أكَلَ عَرْقًا، أوْ لَحْمًا، ثُمَّ صَلَّى ولَمْ يَتَوَضَّأْ، ولَمْ يَمَسَّ مَاءً.\n\n- الراوي: عبدالله بن عباس\n- حكم الحديث: [صحيح]",
"أنَّ رَسولَ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ أكَلَ كَتِفَ شَاةٍ ثُمَّ صَلَّى ولَمْ يَتَوَضَّأْ.\n\n- الراوي: عبدالله بن عباس\n- حكم الحديث: [صحيح]",
"رَأَيْتُ رَسولَ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ يَحْتَزُّ مِن كَتِفِ شَاةٍ، فأكَلَ منها، فَدُعِيَ إلى الصَّلَاةِ، فَقَامَ وطَرَحَ السِّكِّينَ، وصَلَّى ولَمْ يَتَوَضَّأْ. قالَ ابنُ شِهَابٍ: وحدَّثَني عَلِيُّ بنُ عبدِ اللهِ بنِ عَبَّاسٍ، عن أبِيهِ عن رَسولِ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ بذلك.\n\n- الراوي: عمرو بن امية \n- حكم الحديث: [صحيح]",
"أنَّهُ رَأَى رَسولَ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ يَحْتَزُّ مِن كَتِفٍ يَأْكُلُ منها، ثُمَّ صَلَّى ولَمْ يَتَوَضَّأْ.\n\n- الراوي: عمرو بن امية \n- حكم الحديث: [صحيح]",
"أنَّ النبيَّ صَلَّى اللَّهُ عليه وسلَّمَ أكَلَ عِنْدَهَا كَتِفًا، ثُمَّ صَلَّى ولَمْ يَتَوَضَّأْ.\n\n- الراوي: ميمونة بنت الحارث أم المؤمنين\n- حكم الحديث: [صحيح]",
"أَتَى النبيُّ صَلَّى اللَّهُ عليه وَسَلَّمَ قَبْرَ عبدِ اللهِ بنِ أُبَيٍّ، فأخْرَجَهُ مِن قَبْرِهِ فَوَضَعَهُ علَى رُكْبَتَيْهِ، وَنَفَثَ عليه مِن رِيقِهِ، وَأَلْبَسَهُ قَمِيصَهُ فَاللَّهُ أَعْلَمُ. وفي رواية : جَاءَ النبيُّ صَلَّى اللَّهُ عليه وَسَلَّمَ إلى عبدِ اللهِ بنِ أُبَيٍّ، بَعْدَ ما أُدْخِلَ حُفْرَتَهُ\n\n- الراوي: جابر بن عبدالله \n- حكم الحديث: [صحيح]",
"ما غِرْتُ علَى نِسَاءِ النبيِّ صَلَّى اللَّهُ عليه وسلَّمَ، إلَّا علَى خَدِيجَةَ وإنِّي لَمْ أُدْرِكْهَا\nقالَتْ: وَكانَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ إذَا ذَبَحَ الشَّاةَ، فيَقولُ: أَرْسِلُوا بهَا إلى أَصْدِقَاءِ خَدِيجَةَ قالَتْ: فأغْضَبْتُهُ يَوْمًا، فَقُلتُ: خَدِيجَةَ، فَقالَ: رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ إنِّي قدْ رُزِقْتُ حُبَّهَا.وفي روايةٍ : بهذا الإسْنَادِ نَحْوَ حَديثِ أَبِي أُسَامَةَ إلى قِصَّةِ الشَّاةِ وَلَمْ يَذْكُرِ الزِّيَادَةَ بَعْدَهَا.\n\n- الراوي: عائشة ام المؤمنين \n- حكم الحديث: [صحيح]",
"ما غِرْتُ علَى امْرَأَةٍ ما غِرْتُ علَى خَدِيجَةَ وَلقَدْ هَلَكَتْ قَبْلَ أَنْ يَتَزَوَّجَنِي بثَلَاثِ سِنِينَ، لِما كُنْتُ أَسْمَعُهُ يَذْكُرُهَا، وَلقَدْ أَمَرَهُ رَبُّهُ عَزَّ وَجَلَّ أَنْ يُبَشِّرَهَا ببَيْتٍ مِن قَصَبٍ في الجَنَّةِ، وإنْ كانَ لَيَذْبَحُ الشَّاةَ، ثُمَّ يُهْدِيهَا إلى خَلَائِلِهَا.\n\n- الراوي: عائشة ام المؤمنين \n- حكم الحديث: [صحيح]",
"ما غِرْتُ للنبيِّ صَلَّى اللَّهُ عليه وسلَّمَ علَى امْرَأَةٍ مِن نِسَائِهِ، ما غِرْتُ علَى خَدِيجَةَ لِكَثْرَةِ ذِكْرِهِ إيَّاهَا وَما رَأَيْتُهَا قَطُّ.\n\n- الراوي: عائشة ام المؤمنين \n- حكم الحديث: [صحيح]",
"أنَّهُ سَمِعَ مُعَاوِيَةَ بنَ أَبِي سُفْيَانَ، خَطِيبًا بالمَدِينَةِ، يَعْنِي في قَدْمَةٍ قَدِمَهَا، خَطَبَهُمْ يَومَ عَاشُورَاءَ، فَقالَ: أَيْنَ عُلَمَاؤُكُمْ؟ يا أَهْلَ المَدِينَةِ، سَمِعْتُ رَسولَ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ يقولُ، لِهذا اليَومِ، هذا يَوْمُ عَاشُورَاءَ، وَلَمْ يَكْتُبِ اللَّهُ علَيْكُم صِيَامَهُ، وَأَنَا صَائِمٌ، فمَن أَحَبَّ مِنكُم أَنْ يَصُومَ فَلْيَصُمْ، وَمَن أَحَبَّ أَنْ يُفْطِرَ فَلْيُفْطِرْ. [وفي رواية]: سَمِعَ النبيَّ صَلَّى اللَّهُ عليه وسلَّمَ يقولُ في مِثْلِ هذا اليَومِ: إنِّي صَائِمٌ، فمَن شَاءَ أَنْ يَصُومَ فَلْيَصُمْ وَلَمْ يَذْكُرْ بَاقِي حَديثِ مَالِكٍ، وَيُونُسَ.\n\n- الراوي: معاوية بن أبي سفيان \n- حكم الحديث: [صحيح]",
"قَدِمَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ المَدِينَةَ، فَوَجَدَ اليَهُودَ يَصُومُونَ يَومَ عَاشُورَاءَ فَسُئِلُوا عن ذلكَ؟ فَقالوا: هذا اليَوْمُ الذي أَظْهَرَ اللَّهُ فيه مُوسَى، وَبَنِي إسْرَائِيلَ علَى فِرْعَوْنَ، فَنَحْنُ نَصُومُهُ تَعْظِيمًا له، فَقالَ النبيُّ صَلَّى اللَّهُ عليه وسلَّمَ: نَحْنُ أَوْلَى بمُوسَى مِنكُم فأمَرَ بصَوْمِهِ\n\n- الراوي: عبدالله بن عباس \n- حكم الحديث: [صحيح]",
"أنَّ رَسُولَ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ قَدِمَ المَدِينَةَ فَوَجَدَ اليَهُودَ صِيَامًا، يَومَ عَاشُورَاءَ، فَقالَ لهمْ رَسُولُ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ: ما هذا اليَوْمُ الذي تَصُومُونَهُ؟ فَقالوا: هذا يَوْمٌ عَظِيمٌ، أَنْجَى اللَّهُ فيه مُوسَى وَقَوْمَهُ، وَغَرَّقَ فِرْعَوْنَ وَقَوْمَهُ، فَصَامَهُ مُوسَى شُكْرًا، فَنَحْنُ نَصُومُهُ، فَقالَ رَسُولُ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ: فَنَحْنُ أَحَقُّ وَأَوْلَى بمُوسَى مِنكُم فَصَامَهُ رَسُولُ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ، وَأَمَرَ بصِيَامِهِ.\n\n- الراوي: معاوية بن أبي سفيان \n- حكم الحديث: [صحيح]",
"بيْنَما عُمَرُ بنُ الخَطَّابِ يَخْطُبُ النَّاسَ يَومَ الجُمُعَةِ، إذْ دَخَلَ عُثْمَانُ بنُ عَفَّانَ، فَعَرَّضَ به عُمَرُ، فَقالَ: ما بَالُ رِجَالٍ يَتَأَخَّرُونَ بَعْدَ النِّدَاءِ؟ فَقالَ عُثْمَانُ: يا أَمِيرَ المُؤْمِنِينَ ما زِدْتُ حِينَ سَمِعْتُ النِّدَاءَ أَنْ تَوَضَّأْتُ، ثُمَّ أَقْبَلْتُ، فَقالَ عُمَرُ: وَالْوُضُوءَ أَيْضًا، أَلَمْ تَسْمَعُوا رَسولَ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ يقولُ: إذَا جَاءَ أَحَدُكُمْ إلى الجُمُعَةِ فَلْيَغْتَسِلْ.\n\n- الراوي: عمر بن الخطاب \n- حكم الحديث: [صحيح]",
"أنَّ عُمَرَ بنَ الخَطَّابِ، بيْنَا هو يَخْطُبُ النَّاسَ يَومَ الجُمُعَةِ دَخَلَ رَجُلٌ مِن أَصْحَابِ رَسولِ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ، فَنَادَاهُ عُمَرُ: أَيَّةُ سَاعَةٍ هذِه؟ فَقالَ: إنِّي شُغِلْتُ اليَومَ، فَلَمْ أَنْقَلِبْ إلى أَهْلِي حتَّى سَمِعْتُ النِّدَاءَ، فَلَمْ أَزِدْ علَى أَنْ تَوَضَّأْتُ، قالَ عُمَرُ: وَالْوُضُوءَ أَيْضًا، وَقَدْ عَلِمْتَ أنَّ رَسولَ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ كانَ يَأْمُرُ بالغُسْلِ.\n\n- الراوي: عمر بن الخطاب \n- حكم الحديث: [صحيح]",
"لقَدْ هَمَمْتُ أَنْ آمُرَ فِتْيَانِي أَنْ يَسْتَعِدُّوا لي بحُزَمٍ مِن حَطَبٍ، ثُمَّ آمُرَ رَجُلًا يُصَلِّي بالنَّاسِ، ثُمَّ تُحَرَّقُ بُيُوتٌ علَى مَن فِيهَا.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"لقَدْ هَمَمْتُ أنَّ آمُرَ رَجُلًا يُصَلِّي بالنَّاسِ، ثُمَّ أُخَالِفَ إلى رِجَالٍ يَتَخَلَّفُونَ عَنْهَا، فَآمُرَ بهِمْ فيُحَرِّقُوا عليهم، بحُزَمِ الحَطَبِ بُيُوتَهُمْ، ولو عَلِمَ أَحَدُهُمْ أنَّهُ يَجِدُ عَظْمًا سَمِينًا لَشَهِدَهَا يَعْنِي صَلَاةَ العِشَاءِ.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"إذا أُقِيمَتِ الصَّلاةُ فلا تَأْتُوها تَسْعَوْنَ، وأْتُوها تَمْشُونَ وعَلَيْكُمُ السَّكِينَةُ، فَما أدْرَكْتُمْ فَصَلُّوا، وما فاتَكُمْ فأتِمُّوا.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"إذا ثُوِّبَ بالصَّلاةِ فلا يَسْعَ إلَيْها أحَدُكُمْ، ولَكِنْ لِيَمْشِ وعليه السَّكِينَةُ والْوَقارُ، صَلِّ ما أدْرَكْتَ، واقْضِ ما سَبَقَكَ.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"إذا ثُوِّبَ لِلصَّلاةِ فلا تَأْتُوها وأَنْتُمْ تَسْعَوْنَ، وأْتُوها وعَلَيْكُمُ السَّكِينَةُ، فَما أدْرَكْتُمْ فَصَلُّوا، وما فاتَكُمْ فأتِمُّوا، فإنَّ أحَدَكُمْ إذا كانَ يَعْمِدُ إلى الصَّلاةِ فَهو في صَلاةٍ.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"إذا نُودِيَ بالصَّلاةِ فَأْتُوها وأَنْتُمْ تَمْشُونَ، وعَلَيْكُمُ السَّكِينَةُ، فَما أدْرَكْتُمْ فَصَلُّوا وما فاتَكُمْ فأتِمُّوا.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"بيْنَما نَحْنُ نُصَلِّي مع رَسولِ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ، فَسَمِعَ جَلَبَةً، فَقالَ: ما شَأْنُكُمْ؟ قالوا: اسْتَعْجَلْنَا إلى الصَّلَاةِ، قالَ: فلا تَفْعَلُوا، إذَا أَتَيْتُمُ الصَّلَاةَ فَعَلَيْكُمُ السَّكِينَةُ، فَما أَدْرَكْتُمْ فَصَلُّوا، وَما سَبَقَكُمْ فأتِمُّوا.\n\n- الراوي: أبو قتادة الحارث بن ربعي  \n- حكم الحديث: [صحيح]",
"إنَّ العَبْدَ، إذا وُضِعَ في قَبْرِهِ، وتَوَلَّى عنْه أصْحابُهُ، إنَّه لَيَسْمَعُ قَرْعَ نِعالِهِمْ قالَ: يَأْتِيهِ مَلَكانِ فيُقْعِدانِهِ فَيَقُولانِ له: ما كُنْتَ تَقُولُ في هذا الرَّجُلِ؟ قالَ: فأمَّا المُؤْمِنُ، فيَقولُ: أشْهَدُ أنَّه عبدُ اللهِ ورَسولُهُ قالَ: فيُقالُ له: انْظُرْ إلى مَقْعَدِكَ مِنَ النَّارِ، قدْ أبْدَلَكَ اللَّهُ به مَقْعَدًا مِنَ الجَنَّةِ قالَ نَبِيُّ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ: فَيَراهُما جَمِيعًا.\nقالَ قَتادَةُ: وذُكِرَ لنا أنَّه يُفْسَحُ له في قَبْرِهِ سَبْعُونَ ذِراعًا، ويُمْلأُ عليه خَضِرًا، إلى يَومِ يُبْعَثُونَ.\n\n- الراوي: انس بن مالك \n- حكم الحديث: [صحيح]",
"إنَّ المَيِّتَ إذا وُضِعَ في قَبْرِهِ، إنَّه لَيَسْمَعُ خَفْقَ نِعالِهِمْ إذا انْصَرَفُوا.وفي رواية : إنَّ العَبْدَ إذا وُضِعَ في قَبْرِهِ وتَوَلَّى عنْه أصْحابُهُ.\n\n- الراوي:  انس بن مالك\n- حكم الحديث: [صحيح]",
"عَنْ أَبِي هُرَيْرَةَ، قالَ: أُرْسِلَ مَلَكُ المَوْتِ إلى مُوسَى عليه السَّلَامُ، فَلَمَّا جَاءَهُ صَكَّهُ فَفَقَأَ عَيْنَهُ، فَرَجَعَ إلى رَبِّهِ فَقالَ: أَرْسَلْتَنِي إلى عَبْدٍ لا يُرِيدُ المَوْتَ، قالَ فَرَدَّ اللَّهُ إلَيْهِ عَيْنَهُ وَقالَ: ارْجِعْ إلَيْهِ، فَقُلْ له: يَضَعُ يَدَهُ علَى مَتْنِ ثَوْرٍ، فَلَهُ، بما غَطَّتْ يَدُهُ بكُلِّ شَعْرَةٍ، سَنَةٌ، قالَ: أَيْ رَبِّ ثُمَّ مَهْ؟ قالَ: ثُمَّ المَوْتُ، قالَ: فَالآنَ، فَسَأَلَ اللَّهَ أَنْ يُدْنِيَهُ مِنَ الأرْضِ المُقَدَّسَةِ رَمْيَةً بحَجَرٍ، فَقالَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ: فلوْ كُنْتُ ثَمَّ، لأَرَيْتُكُمْ قَبْرَهُ إلى جَانِبِ الطَّرِيقِ، تَحْتَ الكَثِيبِ الأحْمَرِ.\n\n- الراوي: طاووس بن كيسان اليماني  \n- حكم الحديث: [صحيح]",
"جَاءَ مَلَكُ المَوْتِ إلى مُوسَى عليه السَّلَامُ. فَقالَ له: أَجِبْ رَبَّكَ قالَ فَلَطَمَ مُوسَى عليه السَّلَامُ عَيْنَ مَلَكِ المَوْتِ فَفَقَأَهَا، قالَ فَرَجَعَ المَلَكُ إلى اللهِ تَعَالَى فَقالَ: إنَّكَ أَرْسَلْتَنِي إلى عَبْدٍ لكَ لا يُرِيدُ المَوْتَ، وَقَدْ فَقَأَ عَيْنِي، قالَ فَرَدَّ اللَّهُ إِلَيْهِ عَيْنَهُ وَقالَ: ارْجِعْ إلى عَبْدِي فَقُلْ: الحَيَاةَ تُرِيدُ؟ فإنْ كُنْتَ تُرِيدُ الحَيَاةَ فَضَعْ يَدَكَ علَى مَتْنِ ثَوْرٍ، فَما تَوَارَتْ يَدُكَ مِن شَعْرَةٍ، فإنَّكَ تَعِيشُ بهَا سَنَةً، قالَ: ثُمَّ مَهْ؟ قالَ: ثُمَّ تَمُوتُ، قالَ: فَالآنَ مِن قَرِيبٍ، رَبِّ أَمِتْنِي مِنَ الأرْضِ المُقَدَّسَةِ، رَمْيَةً بحَجَرٍ، قالَ رَسُولُ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ: وَاللَّهِ لو أَنِّي عِنْدَهُ لأَرَيْتُكُمْ قَبْرَهُ إلى جَانِبِ الطَّرِيقِ، عِنْدَ الكَثِيبِ الأحْمَرِ.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"مُرَّ بجَنَازَةٍ فَأُثْنِيَ عَلَيْهَا خَيْرًا، فَقالَ نَبِيُّ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ: وَجَبَتْ، وَجَبَتْ، وَجَبَتْ، وَمُرَّ بجَنَازَةٍ فَأُثْنِيَ عَلَيْهَا شَرًّا، فَقالَ نَبِيُّ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ: وَجَبَتْ، وَجَبَتْ، وَجَبَتْ، قالَ عُمَرُ: فِدًى لكَ أَبِي وَأُمِّي، مُرَّ بجَنَازَةٍ، فَأُثْنِيَ عَلَيْهَا خَيْرٌ، فَقُلْتَ: وَجَبَتْ، وَجَبَتْ، وَجَبَتْ، وَمُرَّ بجَنَازَةٍ، فَأُثْنِيَ عَلَيْهَا شَرٌّ، فَقُلْتَ: وَجَبَتْ، وَجَبَتْ، وَجَبَتْ؟ فَقالَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ: مَن أَثْنَيْتُمْ عليه خَيْرًا وَجَبَتْ له الجَنَّةُ، وَمَن أَثْنَيْتُمْ عليه شَرًّا وَجَبَتْ له النَّارُ، أَنْتُمْ شُهَدَاءُ اللهِ في الأرْضِ، أَنْتُمْ شُهَدَاءُ اللهِ في الأرْضِ، أَنْتُمْ شُهَدَاءُ اللهِ في الأرْضِ.\n\n- الراوي: انس بن مالك \n- حكم الحديث: [صحيح]",
"بَعَثَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ رَجُلًا مِن أَسْلَمَ يَومَ عَاشُورَاءَ، فأمَرَهُ أَنْ يُؤَذِّنَ في النَّاسِ: مَن كانَ لَمْ يَصُمْ، فَلْيَصُمْ وَمَن كانَ أَكَلَ، فَلْيُتِمَّ صِيَامَهُ إلى اللَّيْلِ.\n\n- الراوي: سلمة بن الأكوع \n- حكم الحديث: [صحيح]",
"إنْ كانَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ لَيُصْبِحُ جُنُبًا مِن جِمَاعٍ، غيرِ احْتِلَامٍ في رَمَضَانَ، ثُمَّ يَصُومُ.\n\n- الراوي: عائشة وأم سلمة \n- حكم الحديث: [صحيح]",
"أنَّ مَرْوَانَ أَرْسَلَهُ إلى أُمِّ سَلَمَةَ رَضِيَ اللَّهُ عَنْهَا يَسْأَلُ عَنِ الرَّجُلِ يُصْبِحُ جُنُبًا، أَيَصُومُ؟ فَقالَتْ: كانَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ يُصْبِحُ جُنُبًا مِن جِمَاعٍ، لا مِن حُلُمٍ، ثُمَّ لا يُفْطِرُ وَلَا يَقْضِي.\n\n- الراوي: أم سلمة أم المؤمنين \n- حكم الحديث: [صحيح]",
"قَدْ كانَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ يُدْرِكُهُ الفَجْرُ في رَمَضَانَ وَهو جُنُبٌ، مِن غيرِ حُلُمٍ، فَيَغْتَسِلُ وَيَصُومُ.\n\n- الراوي:  عائشة أم المؤمنين \n- حكم الحديث: [صحيح]",
"أنَّهُ سَأَلَ أُمَّ سَلَمَةَ رَضِيَ اللَّهُ عَنْهَا، عَنِ الرَّجُلِ يُصْبِحُ جُنُبًا أَيَصُومُ، قالَتْ: كانَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ يُصْبِحُ جُنُبًا مِن غيرِ احْتِلَامٍ، ثُمَّ يَصُومُ.\n\n- الراوي:  أم سلمة أم المؤمنين \n- حكم الحديث: [صحيح]",
"سَمِعْتُ أَبَا هُرَيْرَةَ رَضِيَ اللَّهُ عنْه، يَقُصُّ يقولُ في قَصَصِهِ: مَن أَدْرَكَهُ الفَجْرُ جُنُبًا فلا يَصُمْ، فَذَكَرْتُ ذلكَ لِعَبْدِ الرَّحْمَنِ بنِ الحَارِثِ، لأَبِيهِ، فأنْكَرَ ذلكَ، فَانْطَلَقَ عبدُ الرَّحْمَنِ وَانْطَلَقْتُ معهُ، حتَّى دَخَلْنَا علَى عَائِشَةَ وَأُمِّ سَلَمَةَ رَضِيَ اللَّهُ عنْهمَا، فَسَأَلَهُما عبدُ الرَّحْمَنِ عن ذلكَ، قالَ: فَكِلْتَاهُما قالَتْ: كانَ النبيُّ صَلَّى اللَّهُ عليه وسلَّمَ يُصْبِحُ جُنُبًا مِن غيرِ حُلُمٍ، ثُمَّ يَصُومُ قالَ: فَانْطَلَقْنَا حتَّى دَخَلْنَا علَى مَرْوَانَ، فَذَكَرَ ذلكَ له عبدُ الرَّحْمَنِ، فَقالَ مَرْوَانُ: عَزَمْتُ عَلَيْكَ إلَّا ما ذَهَبْتَ إلى أَبِي هُرَيْرَةَ، فَرَدَدْتَ عليه ما يقولُ: قالَ: فَجِئْنَا أَبَا هُرَيْرَةَ، وَأَبُو بَكْرٍ حَاضِرُ ذلكَ كُلِّهِ، قالَ: فَذَكَرَ له عبدُ الرَّحْمَنِ، فَقالَ أَبُو هُرَيْرَةَ أَهُما قالَتَاهُ لكَ قالَ نَعَمْ قالَ هُما أَعْلَمُ\nثُمَّ رَدَّ أَبُو هُرَيْرَةَ ما كانَ يقولُ في ذلكَ إلى الفَضْلِ بنِ العَبَّاسِ. فَقالَ أَبُو هُرَيْرَةَ سَمِعْتُ ذلكَ مِنَ الفَضْلِ وَلَمْ أَسْمَعْهُ مِنَ النبيِّ صلى الله عليه وسلم.\nقالَ فَرَجَعَ أَبُو هُرَيْرَةَ عَمَّا كانَ يقولُ في ذلكَ.\n\n- الراوي: عائشة وأم سلمة \n- حكم الحديث: [صحيح]",
"فَفَطِنَتْ بهِمْ عَائِشَةُ فَسَبَّتْهُمْ، فَقالَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ: مَهْ يا عَائِشَةُ، فإنَّ اللَّهَ لا يُحِبُّ الفُحْشَ وَالتَّفَحُّشَ.\nوَزَادَ فأنْزَلَ اللَّهُ عَزَّ وَجَلَّ {وَإِذَا جَاؤُوكَ حَيَّوْكَ بما لَمْ يُحَيِّكَ به اللَّهُ} [المجادلة: 8] إلى آخِرِ الآيَةِ.\n\n- الراوي:  عائشة أم المؤمنين \n- حكم الحديث: [صحيح]",
"أَتَى النبيَّ صَلَّى اللَّهُ عليه وسلَّمَ أُنَاسٌ مِنَ اليَهُودِ فَقالوا: السَّامُ عَلَيْكَ يا أَبَا القَاسِمِ قالَ: وَعلَيْكُم قالَتْ عَائِشَةُ: قُلتُ بَلْ عَلَيْكُمُ السَّامُ وَالذَّامُ، فَقالَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ: يا عَائِشَةُ لا تَكُونِي فَاحِشَةً فَقالَتْ: ما سَمِعْتَ ما قالوا؟ فَقالَ: أَوَليسَ قدْ رَدَدْتُ عليهمِ الذي قالوا، قُلتُ: وَعلَيْكُم.\n\n- الراوي: عائشة أم المؤمنين \n- حكم الحديث: [صحيح]",
"اسْتَأْذَنَ رَهْطٌ مِنَ اليَهُودِ علَى رَسولِ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ فَقالوا: السَّامُ علَيْكُم، فَقالَتْ عَائِشَةُ: بَلْ عَلَيْكُمُ السَّامُ وَاللَّعْنَةُ، فَقالَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ: يا عَائِشَةُ إنَّ اللَّهَ يُحِبُّ الرِّفْقَ في الأمْرِ كُلِّهِ قالَتْ: أَلَمْ تَسْمَعْ ما قالوا؟ قالَ: قدْ قُلتُ وَعلَيْكُم.وفي روايةٍ : قدْ قُلتُ علَيْكُم وَلَمْ يَذْكُرُوا الوَاوَ.\n\n- الراوي: عائشة أم المؤمنين \n- حكم الحديث: [صحيح]",
"سَلَّمَ نَاسٌ مِن يَهُودَ علَى رَسولِ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ، فَقالوا: السَّامُ عَلَيْكَ يا أَبَا القَاسِمِ، فَقالَ: وَعلَيْكُم فَقالَتْ عَائِشَةُ: وَغَضِبَتْ أَلَمْ تَسْمَعْ ما قالوا؟ قالَ: بَلَى، قدْ سَمِعْتُ فَرَدَدْتُ عليهم وإنَّا نُجَابُ عليهم وَلَا يُجَابُونَ عَلَيْنَا.\n\n- الراوي: جابر بن عبدالله \n- حكم الحديث: [صحيح]",
"أَتَى جِبْرِيلُ النبيَّ صَلَّى اللَّهُ عليه وسلَّمَ، فَقالَ: يا رَسولَ اللهِ، هذِه خَدِيجَةُ قدْ أَتَتْكَ معهَا إنَاءٌ فيه إدَامٌ، أَوْ طَعَامٌ، أَوْ شَرَابٌ، فَإِذَا هي أَتَتْكَ، فَاقْرَأْ عَلَيْهَا السَّلَامَ مِن رَبِّهَا عَزَّ وَجَلَّ، وَمِنِّي، وَبَشِّرْهَا ببَيْتٍ في الجَنَّةِ مِن قَصَبٍ، لا صَخَبَ فيه وَلَا نَصَبَ.\nقالَ أَبُو بَكْرٍ في رِوَايَتِهِ: عن أَبِي هُرَيْرَةَ وَلَمْ يَقُلْ: سَمِعْتُ وَلَمْ يَقُلْ في الحَديثِ: وَمِنِّي.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"قُلتُ لِعَبْدِ اللهِ بنِ أَبِي أَوْفَى، أَكانَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ بَشَّرَ خَدِيجَةَ ببَيْتٍ في الجَنَّةِ؟ قالَ: نَعَمْ، بَشَّرَهَا ببَيْتٍ في الجَنَّةِ مِن قَصَبٍ لا صَخَبَ فيه وَلَا نَصَبَ.\n\n- الراوي: إسماعيل بن أبي خالد\n- حكم الحديث: [صحيح]",
"بَشَّرَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ خَدِيجَةَ بنْتَ خُوَيْلِدٍ ببَيْتٍ في الجَنَّةِ.\n\n- الراوي: عائشة ام المؤمنين \n- حكم الحديث: [صحيح]",
"سَمِعْتُ رَسولَ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ يقولُ: مَن ماتَ يُشْرِكُ باللَّهِ شيئًا دَخَلَ النَّارَ.\nوَقُلتُ أنا: ومَن ماتَ لا يُشْرِكُ باللَّهِ شيئًا دَخَلَ الجَنَّةَ.\n\n- الراوي: عبدالله بن مسعود \n- حكم الحديث: [صحيح]",
"غَدْوَةٌ، أوْ رَوْحَةٌ في سَبيلِ اللهِ، خَيْرٌ مِنَ الدُّنْيا وما فيها.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"والْغَدْوَةَ يَغْدُوها العَبْدُ في سَبيلِ اللهِ، خَيْرٌ مِنَ الدُّنْيا وما فيها.\n\n- الراوي: سهل بن سعد الساعدي \n- حكم الحديث: [صحيح]",
"وَلَرَوْحَةٌ في سَبيلِ اللهِ، أوْ غَدْوَةٌ خَيْرٌ مِنَ الدُّنْيا وما فيها.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"لَغَدْوَةٌ في سَبيلِ اللهِ، أوْ رَوْحَةٌ، خَيْرٌ مِنَ الدُّنْيا وما فيها.\n\n- الراوي: أنس بن مالك\n- حكم الحديث: [صحيح]",
"غَدْوَةٌ في سَبيلِ اللهِ، أوْ رَوْحَةٌ، خَيْرٌ ممَّا طَلَعَتْ عليه الشَّمْسُ وغَرَبَتْ.\n\n- الراوي: أبو أيوب الأنصاري\n- حكم الحديث: [صحيح]",
"التَّسْبِيحُ لِلرِّجالِ والتَّصْفِيقُ لِلنِّساءِ.\nزادَ حَرْمَلَةُ في رِوايَتِهِ: قالَ ابنُ شِهابٍ: وقدْ رَأَيْتُ رِجالًا مِن أهْلِ العِلْمِ يُسَبِّحُونَ ويُشِيرُونَ. وفي رواية: بمِثْلِهِ وزادَ: في الصَّلاةِ.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"أنَّ رَسولَ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ ذَكَرَ يَومَ الجُمُعَةِ، فَقالَ: فيه سَاعَةٌ، لا يُوَافِقُهَا عَبْدٌ مُسْلِمٌ، وَهو يُصَلِّي، يَسْأَلُ اللَّهَ شيئًا، إلَّا أَعْطَاهُ إيَّاهُ.\nزَادَ قُتَيْبَةُ في رِوَايَتِهِ: وَأَشَارَ بيَدِهِ يُقَلِّلُهَا.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"إنَّ في الجُمُعَةِ لَساعَةً، لا يُوافِقُها مُسْلِمٌ، قائِمٌ يُصَلِّي، يَسْأَلُ اللَّهَ خَيْرًا، إلَّا أعْطاهُ إيَّاهُ.\nوَقالَ بيَدِهِ: يُقَلِّلُها يُزَهِّدُها.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"إنَّ في الجُمُعَةِ لَساعَةً، لا يُوافِقُها مُسْلِمٌ، يَسْأَلُ اللَّهَ فيها خَيْرًا، إلَّا أعْطاهُ إيَّاهُ، قالَ: وهي ساعَةٌ خَفِيفَةٌ. وفي رواية: ولَمْ يَقُلْ: وهي ساعَةٌ خَفِيفَةٌ.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"جَاءَ رَجُلٌ وَالنبيُّ صَلَّى اللَّهُ عليه وسلَّمَ علَى المِنْبَرِ يَومَ الجُمُعَةِ يَخْطُبُ، فَقالَ له أَرَكَعْتَ رَكْعَتَيْنِ؟ قالَ: لَا، فَقالَ: ارْكَعْ.\n\n- الراوي: جابر بن عبدالله \n- حكم الحديث: [صحيح]",
"بيْنَا النبيُّ صَلَّى اللَّهُ عليه وسلَّمَ يَخْطُبُ يَومَ الجُمُعَةِ، إذْ جَاءَ رَجُلٌ، فَقالَ له النبيُّ صَلَّى اللَّهُ عليه وسلَّمَ: أَصَلَّيْتَ؟ يا فُلَانُ قالَ: لَا، قالَ: قُمْ فَارْكَعْ. [وفي رواية]: وَلَمْ يَذْكُرِ الرَّكْعَتَيْنِ.\n\n- الراوي: جابر بن عبدالله\n- حكم الحديث: [صحيح]",
"دَخَلَ رَجُلٌ المَسْجِدَ، وَرَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ يَخْطُبُ يَومَ الجُمُعَةِ، فَقالَ: أَصَلَّيْتَ؟ قالَ: لَا، قالَ: قُمْ فَصَلِّ الرَّكْعَتَيْنِ.\nوفي رِوَايَةِ قُتَيْبَةَ، قالَ: صَلِّ رَكْعَتَيْنِ.\n\n- الراوي: جابر بن عبدالله \n- حكم الحديث: [صحيح]",
"جاءَ سُلَيْكٌ الغَطَفانِيُّ يَومَ الجُمُعَةِ، ورَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ قاعِدٌ علَى المِنْبَرِ، فَقَعَدَ سُلَيْكٌ قَبْلَ أنْ يُصَلِّيَ، فقالَ له النبيُّ صَلَّى اللَّهُ عليه وسلَّمَ: أرَكَعْتَ رَكْعَتَيْنِ؟ قالَ: لا، قالَ: قُمْ فارْكَعْهُما.\n\n- الراوي: جابر بن عبدالله\n- حكم الحديث: [صحيح]",
"جاءَ سُلَيْكٌ الغَطَفانِيُّ يَومَ الجُمُعَةِ، ورَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ قاعِدٌ علَى المِنْبَرِ، فَقَعَدَ سُلَيْكٌ قَبْلَ أنْ يُصَلِّيَ، فقالَ له النبيُّ صَلَّى اللَّهُ عليه وسلَّمَ: أرَكَعْتَ رَكْعَتَيْنِ؟ قالَ: لا، قالَ: قُمْ فارْكَعْهُما.\n\n- الراوي: جابر بن عبدالله \n- حكم الحديث: [صحيح]",
"أنَّ أَعْرَابِيًّا أَتَى رَسولَ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ، فَقالَ: يا رَسولَ اللهِ، إنَّ امْرَأَتي وَلَدَتْ غُلَامًا أَسْوَدَ، وإنِّي أَنْكَرْتُهُ، فَقالَ له النبيُّ صَلَّى اللَّهُ عليه وسلَّمَ: هلْ لكَ مِن إبِلٍ؟ قالَ: نَعَمْ، قالَ: ما أَلْوَانُهَا؟ قالَ: حُمْرٌ، قالَ: فَهلْ فِيهَا مِن أَوْرَقَ؟ قالَ: نَعَمْ، قالَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ: فأنَّى هُوَ؟ قالَ: لَعَلَّهُ يا رَسولَ اللهِ، يَكونُ نَزَعَهُ عِرْقٌ له، فَقالَ له النبيُّ صَلَّى اللَّهُ عليه وسلَّمَ: وَهذا لَعَلَّهُ يَكونُ نَزَعَهُ عِرْقٌ له.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"جَاءَ رَجُلٌ مِن بَنِي فَزَارَةَ إلى النبيِّ صَلَّى اللَّهُ عليه وسلَّمَ، فَقالَ: إنَّ امْرَأَتي وَلَدَتْ غُلَامًا أَسْوَدَ، فَقالَ النبيُّ صَلَّى اللَّهُ عليه وسلَّمَ: هلْ لكَ مِن إبِلٍ؟ قالَ: نَعَمْ، قالَ: فَما أَلْوَانُهَا؟ قالَ: حُمْرٌ، قالَ: هلْ فِيهَا مِن أَوْرَقَ؟ قالَ: إنَّ فِيهَا لَوُرْقًا، قالَ: فأنَّى أَتَاهَا ذلكَ؟ قالَ: عَسَى أَنْ يَكونَ نَزَعَهُ عِرْقٌ، قالَ: وَهذا عَسَى أَنْ يَكونَ نَزَعَهُ عِرْقٌ.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"يَا رَسولَ اللهِ، وَلَدَتِ امْرَأَتي غُلَامًا أَسْوَدَ، وَهو حِينَئِذٍ يُعَرِّضُ بأَنْ يَنْفِيَهُ.\nوَزَادَ في آخِرِ الحَديثِ، وَلَمْ يُرَخِّصْ له في الانْتِفَاءِ منه.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"كُنَّا نَتَكَلَّمُ في الصَّلَاةِ يُكَلِّمُ الرَّجُلُ صَاحِبَهُ وهو إلى جَنْبِهِ في الصَّلَاةِ حتَّى نَزَلَتْ {وَقُومُوا لِلَّهِ قَانِتِينَ} [البقرة:238] فَأُمِرْنَا بالسُّكُوتِ، ونُهِينَا عَنِ الكَلَامِ.\n\n- الراوي: زيد بن ارقم \n- حكم الحديث: [صحيح]",
"كُنَّا عِنْدَ رَسولِ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ، فَقالَ: أَخْبِرُونِي بشَجَرَةٍ شِبْهِ، أَوْ كَالرَّجُلِ المُسْلِمِ لا يَتَحَاتُّ وَرَقُهَا. قالَ إبْرَاهِيمُ: لَعَلَّ مُسْلِمًا قالَ: وَتُؤْتي أُكُلَهَا، وَكَذَا وَجَدْتُ عِنْدَ غيرِي أَيْضًا، وَلَا تُؤْتي أُكُلَهَا كُلَّ حِينٍ.\nقالَ ابنُ عُمَرَ: فَوَقَعَ في نَفْسِي أنَّهَا النَّخْلَةُ، وَرَأَيْتُ أَبَا بَكْرٍ وَعُمَرَ لا يَتَكَلَّمَانِ، فَكَرِهْتُ أَنْ أَتَكَلَّمَ، أَوْ أَقُولَ شيئًا، فَقالَ عُمَرُ: لأَنْ تَكُونَ قُلْتَهَا أَحَبُّ إلَيَّ مِن كَذَا وَكَذَا.\n\n- الراوي: عبدالله بن عمر\n- حكم الحديث: [صحيح]",
"نَهَى رَسولُ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ عَنِ الحَنْتَمِ، وَهي الجَرَّةُ، وَعَنِ الدُّبَّاءِ، وَهي القَرْعَةُ، وَعَنِ المُزَفَّتِ، وَهو المُقَيَّرُ، وَعَنِ النَّقِيرِ، وَهي النَّخْلَةُ تُنْسَحُ نَسْحًا، وَتُنْقَرُ نَقْرًا، وَأَمَرَ أَنْ يُنْتَبَذَ في الأسْقِيَةِ.\n\n- الراوي: عبدالله بن عمر \n- حكم الحديث: [صحيح]",
"أَخَذَ عَلَيْنَا رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ مع البَيْعَةِ، أَلَّا نَنُوحَ، فَما وَفَتْ مِنَّا امْرَأَةٌ، إلَّا خَمْسٌ: أُمُّ سُلَيْمٍ، وَأُمُّ العَلَاءِ، وَابْنَةُ أَبِي سَبْرَةَ، امْرَأَةُ مُعَاذٍ، أَوِ ابْنَةُ أَبِي سَبْرَةَ، وَامْرَأَةُ مُعَاذٍ.\n\n- الراوي: أم عطية نسيبة بنت كعب \n- حكم الحديث: [صحيح]",
"أَخَذَ عَلَيْنَا رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ في البَيْعَةِ أَلَّا تَنُحْنَ، فَما وَفَتْ مِنَّا غَيْرُ خَمْسٍ، منهنَّ أُمُّ سُلَيْمٍ.\n\n- الراوي:  أم عطية نسيبة بنت كعب \n- حكم الحديث: [صحيح]",
"السَّفَرُ قِطْعَةٌ مِنَ العَذابِ، يَمْنَعُ أحَدَكُمْ نَوْمَهُ وطَعامَهُ وشَرابَهُ، فإذا قَضَى أحَدُكُمْ نَهْمَتَهُ مِن وجْهِهِ، فَلْيُعَجِّلْ إلى أهْلِهِ، قالَ: نَعَمْ.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"أنَّ قُرَيْشًا كَانَتْ تَصُومُ عَاشُورَاءَ في الجَاهِلِيَّةِ، ثُمَّ أَمَرَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ بصِيَامِهِ، حتَّى فُرِضَ رَمَضَانُ، فَقالَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ: مَن شَاءَ فَلْيَصُمْهُ، وَمَن شَاءَ فَلْيُفْطِرْهُ.\n\n- الراوي: عائشة ام المؤمنين \n- حكم الحديث: [صحيح]",
"أنَّهُ ذُكِرَ عِنْدَ رَسولِ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ يَوْمُ عَاشُورَاءَ فَقالَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ: كانَ يَوْمًا يَصُومُهُ أَهْلُ الجَاهِلِيَّةِ، فمَن أَحَبَّ مِنكُم أَنْ يَصُومَهُ فَلْيَصُمْهُ، وَمَن كَرِهَ فَلْيَدَعْهُ.\n\n- الراوي: عبدالله بن عمر \n- حكم الحديث: [صحيح]",
"أنَّ أَهْلَ الجَاهِلِيَّةِ كَانُوا يَصُومُونَ يَومَ عَاشُورَاءَ، وَأنَّ رَسولَ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ صَامَهُ، وَالْمُسْلِمُونَ قَبْلَ أَنْ يُفْتَرَضَ رَمَضَانُ، فَلَمَّا افْتُرِضَ رَمَضَانُ، قالَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ: إنَّ عَاشُورَاءَ يَوْمٌ مِن أَيَّامِ اللهِ، فمَن شَاءَ صَامَهُ وَمَن شَاءَ تَرَكَهُ.\n\n- الراوي: عبدالله بن عمر \n- حكم الحديث: [صحيح]",
"أنَّهُ سَمِعَ رَسولَ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ يقولُ في يَومِ عَاشُورَاءَ: إنَّ هذا يَوْمٌ كانَ يَصُومُهُ أَهْلُ الجَاهِلِيَّةِ، فمَن أَحَبَّ أَنْ يَصُومَهُ فَلْيَصُمْهُ، وَمَن أَحَبَّ أَنْ يَتْرُكَهُ فَلْيَتْرُكْهُ وَكانَ عبدُ اللهِ رَضِيَ اللَّهُ عنْه لا يَصُومُهُ، إلَّا أَنْ يُوَافِقَ صِيَامَهُ.[وفي رواية]: ذُكِرَ عِنْدَ النبيِّ صَلَّى اللَّهُ عليه وسلَّمَ صَوْمُ يَومِ عَاشُورَاءَ فَذَكَرَ مِثْلَ حَديثِ اللَّيْثِ بنِ سَعْدٍ سَوَاءً.\n\n- الراوي: عبدالله بن عمر \n- حكم الحديث: [صحيح]",
"ذُكِرَ عِنْدَ رَسولِ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ يَوْمُ عَاشُورَاءَ، فَقالَ: ذَاكَ يَوْمٌ كانَ يَصُومُهُ أَهْلُ الجَاهِلِيَّةِ، فمَن شَاءَ صَامَهُ وَمَن شَاءَ تَرَكَهُ.\n\n- الراوي: عبدالله بن عمر \n- حكم الحديث: [صحيح]",
"كانَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ يَأْمُرُنَا بصِيَامِ يَومِ عَاشُورَاءَ، وَيَحُثُّنَا عليه، وَيَتَعَاهَدُنَا عِنْدَهُ، فَلَمَّا فُرِضَ رَمَضَانُ، لَمْ يَأْمُرْنَا، وَلَمْ يَنْهَنَا وَلَمْ يَتَعَاهَدْنَا عِنْدَهُ.\n\n- الراوي: جابر بن سمرة \n- حكم الحديث: [صحيح]",
"كانَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ يَأْمُرُ بصِيَامِهِ قَبْلَ أَنْ يُفْرَضَ رَمَضَانُ، فَلَمَّا فُرِضَ رَمَضَانُ، كانَ مَن شَاءَ صَامَ يَومَ عَاشُورَاءَ وَمَن شَاءَ أَفْطَرَ.\n\n- الراوي: عائشة ام المؤمنين\n- حكم الحديث: [صحيح]",
"عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا، قالَتْ: كَانَتْ قُرَيْشٌ تَصُومُ عَاشُورَاءَ في الجَاهِلِيَّةِ، وَكانَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ يَصُومُهُ، فَلَمَّا هَاجَرَ إلى المَدِينَةِ، صَامَهُ وَأَمَرَ بصِيَامِهِ، فَلَمَّا فُرِضَ شَهْرُ رَمَضَانَ قالَ: مَن شَاءَ صَامَهُ وَمَن شَاءَ تَرَكَهُ.\n\n- الراوي:عائشة ام المؤمنين \n- حكم الحديث: [صحيح]",
"عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا: أنَّ يَومَ عَاشُورَاءَ كانَ يُصَامُ في الجَاهِلِيَّةِ، فَلَمَّا جَاءَ الإسْلَامُ مَن شَاءَ صَامَهُ وَمَن شَاءَ تَرَكَهُ.\n\n- الراوي: عائشة ام المؤمنين \n- حكم الحديث: [صحيح]",
"أنَّ ابْنَ عُمَرَ، كانَ إذَا جَدَّ به السَّيْرُ جَمع بيْنَ المَغْرِبِ وَالْعِشَاءِ بَعْدَ أنَّ يَغِيبَ الشَّفَقُ، ويقولُ: إنَّ رَسولَ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ كانَ إذَا جَدَّ به السَّيْرُ جَمع بيْنَ المَغْرِبِ وَالْعِشَاءِ.\n\n- الراوي: عبدالله بن عمر\n- حكم الحديث: [صحيح]",
"رَأَيْتُ رَسولَ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ يَجْمَعُ بيْنَ المَغْرِبِ وَالْعِشَاءِ إذَا جَدَّ به السَّيْرُ.\n\n- الراوي: عبدالله بن عمر\n- حكم الحديث: [صحيح]",
"إنِّي لأَمْشِي مع عبدِ اللهِ بنِ مَسْعُودٍ بمِنًى، إذْ لَقِيَهُ عُثْمَانُ بنُ عَفَّانَ، فَقالَ: هَلُمَّ يا أَبَا عبدِ الرَّحْمَنِ، قالَ: فَاسْتَخْلَاهُ، فَلَمَّا رَأَى عبدُ اللهِ أَنْ ليسَتْ له حَاجَةٌ، قالَ: قالَ لِي: تَعَالَ يا عَلْقَمَةُ، قالَ: فَجِئْتُ، فَقالَ له عُثْمَانُ: أَلَا نُزَوِّجُكَ يا أَبَا عبدِ الرَّحْمَنِ جَارِيَةً بكْرًا، لَعَلَّهُ يَرْجِعُ إلَيْكَ مِن نَفْسِكَ ما كُنْتَ تَعْهَدُ؟ فَقالَ عبدُ اللهِ: لَئِنْ قُلْتَ ذَاكَ، فَذَكَرَ بمِثْلِ حَديثِ أَبِي مُعَاوِيَةَ.\n\n- الراوي: عبدالله بن مسعود \n- حكم الحديث: [صحيح]",
"دَخَلْتُ أَنَا وَعَمِّي عَلْقَمَةُ، وَالأسْوَدُ علَى عبدِ اللهِ بنِ مَسْعُودٍ قالَ: وَأَنَا شَابٌّ يَومَئذٍ، فَذَكَرَ حَدِيثًا رُئِيتُ أنَّهُ حَدَّثَ به مِن أَجْلِي، قالَ: قالَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ... بمِثْلِ حَديثِ أَبِي مُعَاوِيَةَ.\nوَزَادَ قالَ: فَلَمْ أَلْبَثْ حتَّى تَزَوَّجْتُ. [وفي رواية]: دَخَلْنَا عليه وَأَنَا أَحْدَثُ القَوْمِ. بمِثْلِ حَديثِهِمْ. وَلَمْ يَذْكُرْ: فَلَمْ أَلْبَثْ حتَّى تَزَوَّجْتُ.\n\n- الراوي: عبدالله بن مسعود\n- حكم الحديث: [صحيح]",
"عَنْ أَنَسٍ، أنَّ نَفَرًا مِن أَصْحَابِ النبيِّ صَلَّى اللَّهُ عليه وَسَلَّمَ سَأَلُوا أَزْوَاجَ النبيِّ صَلَّى اللَّهُ عليه وَسَلَّمَ عن عَمَلِهِ في السِّرِّ؟ فَقالَ بَعْضُهُمْ: لا أَتَزَوَّجُ النِّسَاءَ، وَقالَ بَعْضُهُمْ: لا آكُلُ اللَّحْمَ، وَقالَ بَعْضُهُمْ: لا أَنَامُ علَى فِرَاشٍ، فَحَمِدَ اللَّهَ وَأَثْنَى عليه. فَقالَ: ما بَالُ أَقْوَامٍ قالوا كَذَا وَكَذَا؟ لَكِنِّي أُصَلِّي وَأَنَامُ، وَأَصُومُ وَأُفْطِرُ، وَأَتَزَوَّجُ النِّسَاءَ، فمَن رَغِبَ عن سُنَّتي فليسَ مِنِّي.\n\n- الراوي: انس بن مالك \n- حكم الحديث: [صحيح]",
"يَا مَعْشَرَ الشَّبَابِ، مَنِ اسْتَطَاعَ مِنْكُمُ البَاءَةَ فَلْيَتَزَوَّجْ، فإنَّه أَغَضُّ لِلْبَصَرِ، وَأَحْصَنُ لِلْفَرْجِ، وَمَن لَمْ يَسْتَطِعْ فَعليه بالصَّوْمِ، فإنَّه له وِجَاءٌ.\n\n- الراوي: عبدالله بن مسعود\n- حكم الحديث: [صحيح]",
"حدَّثَني البَرَاءُ، وهو غَيْرُ كَذُوبٍ أنَّهُمْ كَانُوا يُصَلُّونَ خَلْفَ رَسولِ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ فَإِذَا رَفَعَ رَأْسَهُ مِنَ الرُّكُوعِ لَمْ أرَ أحَدًا يَحْنِي ظَهْرَهُ، حتَّى يَضَعَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ جَبْهَتَهُ علَى الأرْضِ، ثُمَّ يَخِرُّ مَن ورَاءَهُ سُجَّدًا.\n\n- الراوي: البراء بن عازب \n- حكم الحديث: [صحيح]",
"كُنَّا مع النبيِّ صَلَّى اللَّهُ عليه وسلَّمَ لا يَحْنُو أحَدٌ مِنَّا ظَهْرَهُ، حتَّى نَرَاهُ قدْ سَجَدَ.\nفَقالَ زُهَيْرٌ: حَدَّثَنَا سُفْيَانُ قالَ: حَدَّثَنَا الكُوفِيُّونَ: أبَانُ وغَيْرُهُ قالَ حتَّى نَرَاهُ يَسْجُدُ.\n\n- الراوي: البراء بن عازب \n- حكم الحديث: [صحيح]",
"حَدَّثَنَا البَرَاءُ: أنَّهُمْ كَانُوا يُصَلُّونَ مع رَسولِ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ، فَإِذَا رَكَعَ رَكَعُوا، وإذَا رَفَعَ رَأْسَهُ مِنَ الرُّكُوعِ فَقالَ: سَمِعَ اللَّهُ لِمَن حَمِدَهُ لَمْ نَزَلْ قِيَامًا، حتَّى نَرَاهُ قدْ وضَعَ وجْهَهُ في الأرْضِ ثُمَّ نَتَّبِعُهُ.\n\n- الراوي: البراء بن عازب \n- حكم الحديث: [صحيح]",
"حدَّثَني البَرَاءُ، وهو غَيْرُ كَذُوبٍ قالَ: كانَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ، إذَا قالَ: سَمِعَ اللهِ لِمَن حَمِدَهُ لَمْ يَحْنِ أحَدٌ مِنَّا ظَهْرَهُ، حتَّى يَقَعَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ سَاجِدًا، ثُمَّ نَقَعُ سُجُودًا بَعْدَهُ.\n\n- الراوي: البراء بن عازب\n- حكم الحديث: [صحيح]",
"[يمكن أن يضاف للحديث الذي قبله] وفي رواية: صَلَّى بنَا رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ وأَبُو بَكْرٍ خَلْفَهُ فَإِذَا كَبَّرَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ كَبَّرَ أبو بَكْرٍ لِيُسْمِعَنَا...، ثُمَّ ذَكَرَ نَحْوَ حَديثِ اللَّيْثِ.\n\n- الراوي: جابر بن عبدالله\n- حكم الحديث: [صحيح]",
"لَمَّا مَرِضَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ مَرَضَهُ الذي تُوُفِّيَ فِيهِ.\nوفي حَديثِ ابْنِ مُسْهِرٍ: فَأُتِيَ برَسولِ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ حتَّى أُجْلِسَ إلى جَنْبِهِ وكانَ النبيُّ صَلَّى اللَّهُ عليه وسلَّمَ يُصَلِّي بالنَّاسِ وأَبُو بَكْرٍ يُسْمِعُهُمُ التَّكْبِيرَ.\nوفي حَديثِ عِيسَى: فَجَلَسَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ يُصَلِّي وأَبُو بَكْرٍ إلى جَنْبِهِ وأَبُو بَكْرٍ يُسْمِعُ النَّاسَ.\n\n- الراوي: عائشة ام المؤمنين \n- حكم الحديث: [صحيح]",
"لا يُجْلَدُ أحَدٌ فَوْقَ عَشَرَةِ أسْواطٍ، إلَّا في حَدٍّ مِن حُدُودِ اللَّهِ.\n\n- الراوي:  هانئ بن نيار أبو بردة \n- حكم الحديث: [صحيح]",
"مَن أَحَبَّ لِقَاءَ اللهِ، أَحَبَّ اللَّهُ لِقَاءَهُ، وَمَن كَرِهَ لِقَاءَ اللهِ، كَرِهَ اللَّهُ لِقَاءَهُ فَقُلتُ: يا نَبِيَّ اللهِ أَكَرَاهيةُ المَوْتِ؟ فَكُلُّنَا نَكْرَهُ المَوْتَ، فَقالَ: ليسَ كَذَلِكِ، وَلَكِنَّ المُؤْمِنَ إِذَا بُشِّرَ برَحْمَةِ اللهِ وَرِضْوَانِهِ وَجَنَّتِهِ، أَحَبَّ لِقَاءَ اللهِ، فأحَبَّ اللَّهُ لِقَاءَهُ، وإنَّ الكَافِرَ إِذَا بُشِّرَ بعَذَابِ اللهِ وَسَخَطِهِ، كَرِهَ لِقَاءَ اللهِ، وَكَرِهَ اللَّهُ لِقَاءَهُ.\n\n- الراوي: عائشة أم المؤمنين\n- حكم الحديث: [صحيح]",
"مَن أَحَبَّ لِقَاءَ اللهِ، أَحَبَّ اللَّهُ لِقَاءَهُ، وَمَن كَرِهَ لِقَاءَ اللهِ، كَرِهَ اللَّهُ لِقَاءَهُ، وَالْمَوْتُ قَبْلَ لِقَاءِ اللَّهِ.\n\n- الراوي: عائشة ام المؤمنين \n- حكم الحديث: [صحيح]",
"مَن أَحَبَّ لِقَاءَ اللهِ، أَحَبَّ اللَّهُ لِقَاءَهُ، وَمَن كَرِهَ لِقَاءَ اللهِ، كَرِهَ اللَّهُ لِقَاءَهُ.\n\n- الراوي:عبادة بن الصامت \n- حكم الحديث: [صحيح]",
"مَن أَحَبَّ لِقَاءَ اللهِ، أَحَبَّ اللَّهُ لِقَاءَهُ، وَمَن كَرِهَ لِقَاءَ اللهِ، كَرِهَ اللَّهُ لِقَاءَهُ.\n\n- الراوي: أبو موسى الأشعري \n- حكم الحديث: [صحيح]",
"مَن رَأَى مِن أمِيرِهِ شيئًا يَكْرَهُهُ فَلْيَصْبِرْ، فإنَّه مَن فارَقَ الجَماعَةَ شِبْرًا، فَماتَ، فَمِيتَةٌ جاهِلِيَّةٌ.\n\n- الراوي: عبدالله بن عباس \n- حكم الحديث: [صحيح]",
"مَن كَرِهَ مِن أمِيرِهِ شيئًا، فَلْيَصْبِرْ عليه، فإنَّه ليسَ أحَدٌ مِنَ النَّاسِ خَرَجَ مِنَ السُّلْطانِ شِبْرًا، فَماتَ عليه، إلَّا ماتَ مِيتَةً جاهِلِيَّةً.\n\n- الراوي: عبدالله بن عباس\n- حكم الحديث: [صحيح]",
"مَن حَمَلَ عليْنا السِّلاحَ فليسَ مِنَّا.\n\n- الراوي: عبدالله بن عمر\n- حكم الحديث: [صحيح]",
"مَن سَلَّ عليْنا السَّيْفَ فليسَ مِنَّا.\n\n- الراوي: سلمة بن الأكوع\n- حكم الحديث: [صحيح]",
"مَن حَمَلَ عليْنا السِّلاحَ فليسَ مِنَّا.\n\n- الراوي: أبو موسي الاشعري\n- حكم الحديث: [صحيح]",
"عن عَائِشَةَ، قالَتْ: رُبَّما فَتَلْتُ القَلَائِدَ لِهَدْيِ رَسولِ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ فيُقَلِّدُ هَدْيَهُ، ثُمَّ يَبْعَثُ به، ثُمَّ يُقِيمُ لا يَجْتَنِبُ شيئًا ممَّا يَجْتَنِبُ المُحْرِمُ.\n\n- الراوي: عائشة ام المؤمنين \n- حكم الحديث: [صحيح]",
"كانَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ يَبْعَثُ بالهَدْيِ أَفْتِلُ قَلَائِدَهَا بيَدَيَّ، ثُمَّ لا يُمْسِكُ عن شيءٍ لا يُمْسِكُ عنْه الحَلَالُ.\n\n- الراوي: عائشة ام المؤمنين \n- حكم الحديث: [صحيح]",
"عَنْ عَائِشَةَ، قالَتْ: فَتَلْتُ قَلَائِدَ بُدْنِ رَسولِ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ بيَدَيَّ، ثُمَّ أَشْعَرَهَا وَقَلَّدَهَا، ثُمَّ بَعَثَ بهَا إلى البَيْتِ، وَأَقَامَ بالمَدِينَةِ، فَما حَرُمَ عليه شيءٌ كانَ له حِلًّا.\n\n- الراوي: عائشة ام المؤمنين \n- حكم الحديث: [صحيح]",
"أَهْدَى رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ مَرَّةً إلى البَيْتِ غَنَمًا، فَقَلَّدَهَا.\n\n- الراوي: عائشة ام المؤمنين\n- حكم الحديث: [صحيح]",
"مَن أَهْدَى هَدْيًا حَرُمَ عليه ما يَحْرُمُ علَى الحَاجِّ، حتَّى يُنْحَرَ الهَدْيُ، وَقَدْ بَعَثْتُ بهَدْيِي، فَاكْتُبِي إلَيَّ بأَمْرِكِ، قالَتْ عَمْرَةُ: قالَتْ عَائِشَةُ ليسَ كما قالَ ابنُ عَبَّاسٍ: أَنَا فَتَلْتُ قَلَائِدَ هَدْيِ رَسولِ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ بيَدَيَّ، ثُمَّ قَلَّدَهَا رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ بيَدِهِ، ثُمَّ بَعَثَ بهَا مع أَبِي، فَلَمْ يَحْرُمْ علَى رَسولِ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ شيءٌ أَحَلَّهُ اللَّهُ له، حتَّى نُحِرَ الهَدْيُ.\n\n- الراوي: عائشة ام المؤمنين \n- حكم الحديث: [صحيح]",
"كُنَّا نُقَلِّدُ الشَّاءَ، فَنُرْسِلُ بهَا وَرَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ حَلَالٌ، لَمْ يَحْرُمْ عليه منه شيءٌ.\n\n- الراوي: عائشة ام المؤمنين \n- حكم الحديث: [صحيح]",
"كانَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ يُهْدِي مِنَ المَدِينَةِ فأفْتِلُ قَلَائِدَ هَدْيِهِ، ثُمَّ لا يَجْتَنِبُ شيئًا ممَّا يَجْتَنِبُ المُحْرِمُ. [وفي رواية]: كَأَنِّي أَنْظُرُ إلَيَّ أَفْتِلُ قَلَائِدَ هَدْيِ رَسولِ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ بنَحْوِهِ.\n\n- الراوي: عائشة ام المؤمنين \n- حكم الحديث: [صحيح]",
"سَمِعْتُ عَائِشَةَ، وَهي مِن وَرَاءِ الحِجَابِ تُصَفِّقُ، وَتَقُولُ: كُنْتُ أَفْتِلُ قَلَائِدَ هَدْيِ رَسولِ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ بيَدَيَّ، ثُمَّ يَبْعَثُ بهَا وَما يُمْسِكُ عن شيءٍ، ممَّا يُمْسِكُ عنْه المُحْرِمُ، حتَّى يُنْحَرَ هَدْيُهُ.\n\n- الراوي: عائشة ام المؤمنين \n- حكم الحديث: [صحيح]",
"كُنْتُ أَفْتِلُ قَلَائِدَ هَدْيِ رَسولِ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ بيَدَيَّ هَاتَيْنِ، ثُمَّ لا يَعْتَزِلُ شيئًا وَلَا يَتْرُكُهُ.\n\n- الراوي: عائشة ام المؤمنين \n- حكم الحديث: [صحيح]",
"أنَّ رَسولَ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ لَمَّا بَعَثَ مُعاذًا إلى اليَمَنِ، قالَ: إنَّكَ تَقْدَمُ علَى قَوْمٍ أهْلِ كِتابٍ، فَلْيَكُنْ أوَّلَ ما تَدْعُوهُمْ إلَيْهِ عِبادَةُ اللهِ عزَّ وجلَّ، فإذا عَرَفُوا اللَّهَ، فأخْبِرْهُمْ أنَّ اللَّهَ فَرَضَ عليهم خَمْسَ صَلَواتٍ في يَومِهِمْ ولَيْلَتِهِمْ، فإذا فَعَلُوا، فأخْبِرْهُمْ أنَّ اللَّهَ قدْ فَرَضَ عليهم زَكاةً تُؤْخَذُ مِن أغْنِيائِهِمْ فَتُرَدُّ علَى فُقَرائِهِمْ، فإذا أطاعُوا بها، فَخُذْ منهمْ وتَوَقَّ كَرائِمَ أمْوالِهِمْ.\n\n- الراوي:  عبدالله بن عباس\n- حكم الحديث: [صحيح]",
"إنَّ اللَّهَ عَزَّ وَجَلَّ قدْ وَكَّلَ بالرَّحِمِ مَلَكًا، فيَقولُ: أَيْ رَبِّ نُطْفَةٌ، أَيْ رَبِّ عَلَقَةٌ، أَيْ رَبِّ مُضْغَةٌ، فَإِذَا أَرَادَ اللَّهُ أَنْ يَقْضِيَ خَلْقًا قالَ: قالَ المَلَكُ: أَيْ رَبِّ ذَكَرٌ، أَوْ أُنْثَى؟ شَقِيٌّ، أَوْ سَعِيدٌ؟ فَما الرِّزْقُ؟ فَما الأجَلُ؟ فيُكْتَبُ كَذلكَ في بَطْنِ أُمِّهِ.\n\n- الراوي: انس بن مالك \n- حكم الحديث: [صحيح]",
"اسْتَأْذَنَ عُمَرُ علَى رَسولِ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ، وَعِنْدَهُ نِسَاءٌ مِن قُرَيْشٍ يُكَلِّمْنَهُ وَيَسْتَكْثِرْنَهُ، عَالِيَةً أَصْوَاتُهُنَّ، فَلَمَّا اسْتَأْذَنَ عُمَرُ قُمْنَ يَبْتَدِرْنَ الحِجَابَ، فأذِنَ له رَسولُ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ، وَرَسولُ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ يَضْحَكُ، فَقالَ عُمَرُ: أَضْحَكَ اللَّهُ سِنَّكَ، يا رَسولَ اللهِ، فَقالَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ: عَجِبْتُ مِن هَؤُلَاءِ اللَّاتي كُنَّ عِندِي، فَلَمَّا سَمِعْنَ صَوْتَكَ ابْتَدَرْنَ الحِجَابَ قالَ عُمَرُ: فأنْتَ، يا رَسولَ اللهِ، أَحَقُّ أَنْ يَهَبْنَ، ثُمَّ قالَ عُمَرُ: أَيْ عَدُوَّاتِ أَنْفُسِهِنَّ أَتَهَبْنَنِي وَلَا تَهَبْنَ رَسولَ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ؟ قُلْنَ: نَعَمْ، أَنْتَ أَغْلَظُ وَأَفَظُّ مِن رَسولِ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ، قالَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ: وَالَّذِي نَفْسِي بيَدِهِ ما لَقِيَكَ الشَّيْطَانُ قَطُّ سَالِكًا فَجًّا إلَّا سَلَكَ فَجًّا غيرَ فَجِّكَ.\n\n- الراوي: سعد بن ابي وقاص \n- حكم الحديث: [صحيح]",
"أنَّ عُمَرَ بنَ الخَطَّابِ جَاءَ إلى رَسولِ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ، وَعِنْدَهُ نِسْوَةٌ قدْ رَفَعْنَ أَصْوَاتَهُنَّ علَى رَسولِ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ، فَلَمَّا اسْتَأْذَنَ عُمَرُ ابْتَدَرْنَ الحِجَابَ.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"كانَ أَهْلُ الكِتَابِ يَسْدِلُونَ أَشْعَارَهُمْ، وَكانَ المُشْرِكُونَ يَفْرُقُونَ رُؤُوسَهُمْ، وَكانَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ يُحِبُّ مُوَافَقَةَ أَهْلِ الكِتَابِ فِيما لَمْ يُؤْمَرْ به، فَسَدَلَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ نَاصِيَتَهُ، ثُمَّ فَرَقَ بَعْدُ.\n\n- الراوي: عبدالله بن عباس \n- حكم الحديث: [صحيح]",
"أنَّ رَسُولَ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ دَعَا فَاطِمَةَ ابْنَتَهُ فَسَارَّهَا فَبَكَتْ، ثُمَّ سَارَّهَا فَضَحِكَتْ فَقالَتْ عَائِشَةُ: فَقُلتُ لِفَاطِمَةَ: ما هذا الذي سَارَّكِ به رَسُولُ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ، فَبَكَيْتِ، ثُمَّ سَارَّكِ فَضَحِكْتِ؟ قالَتْ: سَارَّنِي فأخْبَرَنِي بمَوْتِهِ، فَبَكَيْتُ، ثُمَّ سَارَّنِي، فأخْبَرَنِي أَنِّي أَوَّلُ مَن يَتْبَعُهُ مِن أَهْلِهِ فَضَحِكْتُ.\n\n- الراوي: عائشة ام المؤمنين \n- حكم الحديث: [صحيح]",
"كُنَّ أَزْوَاجُ النبيِّ صَلَّى اللَّهُ عليه وَسَلَّمَ عِنْدَهُ، لَمْ يُغَادِرْ منهنَّ وَاحِدَةً، فأقْبَلَتْ فَاطِمَةُ تَمْشِي، ما تُخْطِئُ مِشْيَتُهَا مِن مِشْيَةِ رَسُولِ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ شيئًا، فَلَمَّا رَآهَا رَحَّبَ بهَا، فَقالَ: مَرْحَبًا بابْنَتي ثُمَّ أَجْلَسَهَا عن يَمِينِهِ، أَوْ عن شِمَالِهِ، ثُمَّ سَارَّهَا فَبَكَتْ بُكَاءً شَدِيدًا، فَلَمَّا رَأَى جَزَعَهَا سَارَّهَا الثَّانِيَةَ فَضَحِكَتْ، فَقُلتُ لَهَا: خَصَّكِ رَسُولُ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ مِن بَيْنِ نِسَائِهِ بالسِّرَارِ، ثُمَّ أَنْتِ تَبْكِينَ؟ فَلَمَّا قَامَ رَسُولُ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ، سَأَلْتُهَا ما قالَ لَكِ رَسُولُ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ؟ قالَتْ: ما كُنْتُ أُفْشِي علَى رَسُولِ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ سِرَّهُ، قالَتْ: فَلَمَّا تُوُفِّيَ رَسُولُ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ قُلتُ: عَزَمْتُ عَلَيْكِ، بما لي عَلَيْكِ مِنَ الحَقِّ، لَما حَدَّثْتِنِي ما قالَ لَكِ رَسُولُ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ، فَقالَتْ: أَمَّا الآنَ، فَنَعَمْ، أَمَّا حِينَ سَارَّنِي في المَرَّةِ الأُولَى، فأخْبَرَنِي أنَّ جِبْرِيلَ كانَ يُعَارِضُهُ القُرْآنَ في كُلِّ سَنَةٍ مَرَّةً، أَوْ مَرَّتَيْنِ، وإنَّه عَارَضَهُ الآنَ مَرَّتَيْنِ، وإنِّي لا أُرَى الأجَلَ إلَّا قَدِ اقْتَرَبَ، فَاتَّقِي اللَّهَ وَاصْبِرِي، فإنَّه نِعْمَ السَّلَفُ أَنَا لَكِ قالَتْ: فَبَكَيْتُ بُكَائِي الذي رَأَيْتِ، فَلَمَّا رَأَى جَزَعِي سَارَّنِي الثَّانِيَةَ فَقالَ: يا فَاطِمَةُ أَما تَرْضِيْنَ أَنْ تَكُونِي سَيِّدَةَ نِسَاءِ المُؤْمِنِينَ، أَوْ سَيِّدَةَ نِسَاءِ هذِه الأُمَّةِ قالَتْ: فَضَحِكْتُ ضَحِكِي الذي رَأَيْتِ.\n\n- الراوي: عائشة ام المؤمنين \n- حكم الحديث: [صحيح]",
"اجْتَمع نِسَاءُ النبيِّ صَلَّى اللَّهُ عليه وَسَلَّمَ، فَلَمْ يُغَادِرْ منهنَّ امْرَأَةً، فَجَاءَتْ فَاطِمَةُ تَمْشِي كَأنَّ مِشْيَتَهَا مِشْيَةُ رَسُولِ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ، فَقالَ: مَرْحَبًا بابْنَتي فأجْلَسَهَا عن يَمِينِهِ، أَوْ عن شِمَالِهِ، ثُمَّ إنَّه أَسَرَّ إلَيْهَا حَدِيثًا فَبَكَتْ فَاطِمَةُ، ثُمَّ إنَّه سَارَّهَا فَضَحِكَتْ أَيْضًا، فَقُلتُ لَهَا: ما يُبْكِيكِ؟ فَقالَتْ: ما كُنْتُ لِأُفْشِيَ سِرَّ رَسُولِ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ، فَقُلتُ: ما رَأَيْتُ كَالْيَومِ فَرَحًا أَقْرَبَ مِن حُزْنٍ، فَقُلتُ لَهَا حِينَ بَكَتْ: أَخَصَّكِ رَسُولُ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ بحَديثِهِ دُونَنَا، ثُمَّ تَبْكِينَ؟ وَسَأَلْتُهَا عَمَّا قالَ فَقالَتْ: ما كُنْتُ لِأُفْشِيَ سِرَّ رَسُولِ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ، حتَّى إذَا قُبِضَ سَأَلْتُهَا فَقالَتْ: إنَّه كانَ حدَّثَني أنَّ جِبْرِيلَ كانَ يُعَارِضُهُ بالقُرْآنِ كُلَّ عَامٍ مَرَّةً، وإنَّه عَارَضَهُ به في العَامِ مَرَّتَيْنِ، وَلَا أُرَانِي إلَّا قدْ حَضَرَ أَجَلِي، وإنَّكِ أَوَّلُ أَهْلِي لُحُوقًا بي، وَنِعْمَ السَّلَفُ أَنَا لَكِ، فَبَكَيْتُ لذلكَ، ثُمَّ إنَّه سَارَّنِي، فَقالَ: أَلَا تَرْضَيْنَ أَنْ تَكُونِي سَيِّدَةَ نِسَاءِ المُؤْمِنِينَ، أَوْ سَيِّدَةَ نِسَاءِ هذِه الأُمَّةِ فَضَحِكْتُ لذلكَ.\n\n- الراوي: عائشة ام المؤمنين\n- حكم الحديث: [صحيح]",
"أنَّ النبيَّ صَلَّى اللَّهُ عليه وَسَلَّمَ، خَطَبَ أُمَّ هَانِئٍ، بنْتَ أَبِي طَالِبٍ فَقالَتْ: يا رَسُولَ اللهِ، إنِّي قدْ كَبِرْتُ، وَلِي عِيَالٌ، فَقالَ رَسُولُ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ: خَيْرُ نِسَاءٍ رَكِبْنَ...، ثُمَّ ذَكَرَ بمِثْلِ حَديثِ يُونُسَ غيرَ أنَّهُ قالَ أَحْنَاهُ علَى وَلَدٍ في صِغَرِهِ.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"خَيْرُ نِساءٍ رَكِبْنَ الإبِلَ، قالَ أحَدُهُما: صالِحُ نِساءِ قُرَيْشٍ، وقالَ الآخَرُ: نِساءُ قُرَيْشٍ، أحْناهُ علَى يَتِيمٍ في صِغَرِهِ، وأَرْعاهُ علَى زَوْجٍ في ذاتِ يَدِهِ.تَجِدُونَ مِن شَرِّ النَّاسِ ذا الوَجْهَيْنِ، الذي يَأْتي هَؤُلاءِ بوَجْهٍ، وهَؤُلاءِ بوَجْهٍ. بمِثْلِهِ، غيرَ أنَّه قالَ: أرْعاهُ علَى ولَدٍ في صِغَرِهِ ولَمْ يَقُلْ يَتِيمٍ.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"خَيْرُ نِساءٍ رَكِبْنَ الإبِلَ، صالِحُ نِساءِ قُرَيْشٍ أحْناهُ علَى ولَدٍ في صِغَرِهِ، وأَرْعاهُ علَى زَوْجٍ في ذاتِ يَدِهِ.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"نِساءُ قُرَيْشٍ خَيْرُ نِساءٍ رَكِبْنَ الإبِلَ، أحْناهُ علَى طِفْلٍ، وأَرْعاهُ علَى زَوْجٍ في ذاتِ يَدِهِ.\nقالَ: يقولُ أبو هُرَيْرَةَ علَى إثْرِ ذلكَ: ولَمْ تَرْكَبْ مَرْيَمُ بنْتُ عِمْرانَ بَعِيرًا قَطُّ.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"أَخَذَ عَلَيْنَا رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ مع البَيْعَةِ، أَلَّا نَنُوحَ، فَما وَفَتْ مِنَّا امْرَأَةٌ، إلَّا خَمْسٌ: أُمُّ سُلَيْمٍ، وَأُمُّ العَلَاءِ، وَابْنَةُ أَبِي سَبْرَةَ، امْرَأَةُ مُعَاذٍ، أَوِ ابْنَةُ أَبِي سَبْرَةَ، وَامْرَأَةُ مُعَاذٍ.\n\n- الراوي: أم عطية نسيبة بنت كعب \n- حكم الحديث: [صحيح]",
"أَخَذَ عَلَيْنَا رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ في البَيْعَةِ أَلَّا تَنُحْنَ، فَما وَفَتْ مِنَّا غَيْرُ خَمْسٍ، منهنَّ أُمُّ سُلَيْمٍ.\n\n- الراوي:  أم عطية نسيبة بنت كعب \n- حكم الحديث: [صحيح]",
"السَّفَرُ قِطْعَةٌ مِنَ العَذابِ، يَمْنَعُ أحَدَكُمْ نَوْمَهُ وطَعامَهُ وشَرابَهُ، فإذا قَضَى أحَدُكُمْ نَهْمَتَهُ مِن وجْهِهِ، فَلْيُعَجِّلْ إلى أهْلِهِ، قالَ: نَعَمْ.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"أنَّ قُرَيْشًا كَانَتْ تَصُومُ عَاشُورَاءَ في الجَاهِلِيَّةِ، ثُمَّ أَمَرَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ بصِيَامِهِ، حتَّى فُرِضَ رَمَضَانُ، فَقالَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ: مَن شَاءَ فَلْيَصُمْهُ، وَمَن شَاءَ فَلْيُفْطِرْهُ.\n\n- الراوي: عائشة ام المؤمنين \n- حكم الحديث: [صحيح]",
"أنَّهُ ذُكِرَ عِنْدَ رَسولِ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ يَوْمُ عَاشُورَاءَ فَقالَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ: كانَ يَوْمًا يَصُومُهُ أَهْلُ الجَاهِلِيَّةِ، فمَن أَحَبَّ مِنكُم أَنْ يَصُومَهُ فَلْيَصُمْهُ، وَمَن كَرِهَ فَلْيَدَعْهُ.\n\n- الراوي: عبدالله بن عمر \n- حكم الحديث: [صحيح]",
"أنَّ أَهْلَ الجَاهِلِيَّةِ كَانُوا يَصُومُونَ يَومَ عَاشُورَاءَ، وَأنَّ رَسولَ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ صَامَهُ، وَالْمُسْلِمُونَ قَبْلَ أَنْ يُفْتَرَضَ رَمَضَانُ، فَلَمَّا افْتُرِضَ رَمَضَانُ، قالَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ: إنَّ عَاشُورَاءَ يَوْمٌ مِن أَيَّامِ اللهِ، فمَن شَاءَ صَامَهُ وَمَن شَاءَ تَرَكَهُ.\n\n- الراوي: عبدالله بن عمر \n- حكم الحديث: [صحيح]",
"أنَّهُ سَمِعَ رَسولَ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ يقولُ في يَومِ عَاشُورَاءَ: إنَّ هذا يَوْمٌ كانَ يَصُومُهُ أَهْلُ الجَاهِلِيَّةِ، فمَن أَحَبَّ أَنْ يَصُومَهُ فَلْيَصُمْهُ، وَمَن أَحَبَّ أَنْ يَتْرُكَهُ فَلْيَتْرُكْهُ وَكانَ عبدُ اللهِ رَضِيَ اللَّهُ عنْه لا يَصُومُهُ، إلَّا أَنْ يُوَافِقَ صِيَامَهُ.[وفي رواية]: ذُكِرَ عِنْدَ النبيِّ صَلَّى اللَّهُ عليه وسلَّمَ صَوْمُ يَومِ عَاشُورَاءَ فَذَكَرَ مِثْلَ حَديثِ اللَّيْثِ بنِ سَعْدٍ سَوَاءً.\n\n- الراوي: عبدالله بن عمر \n- حكم الحديث: [صحيح]",
"ذُكِرَ عِنْدَ رَسولِ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ يَوْمُ عَاشُورَاءَ، فَقالَ: ذَاكَ يَوْمٌ كانَ يَصُومُهُ أَهْلُ الجَاهِلِيَّةِ، فمَن شَاءَ صَامَهُ وَمَن شَاءَ تَرَكَهُ.\n\n- الراوي: عبدالله بن عمر \n- حكم الحديث: [صحيح]",
"كانَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ يَأْمُرُنَا بصِيَامِ يَومِ عَاشُورَاءَ، وَيَحُثُّنَا عليه، وَيَتَعَاهَدُنَا عِنْدَهُ، فَلَمَّا فُرِضَ رَمَضَانُ، لَمْ يَأْمُرْنَا، وَلَمْ يَنْهَنَا وَلَمْ يَتَعَاهَدْنَا عِنْدَهُ.\n\n- الراوي: جابر بن سمرة\n- حكم الحديث: [صحيح]",
"كانَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ يَأْمُرُ بصِيَامِهِ قَبْلَ أَنْ يُفْرَضَ رَمَضَانُ، فَلَمَّا فُرِضَ رَمَضَانُ، كانَ مَن شَاءَ صَامَ يَومَ عَاشُورَاءَ وَمَن شَاءَ أَفْطَرَ.\n\n- الراوي: عائشة أم المؤمنين \n- حكم الحديث: [صحيح]",
"عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا، قالَتْ: كَانَتْ قُرَيْشٌ تَصُومُ عَاشُورَاءَ في الجَاهِلِيَّةِ، وَكانَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ يَصُومُهُ، فَلَمَّا هَاجَرَ إلى المَدِينَةِ، صَامَهُ وَأَمَرَ بصِيَامِهِ، فَلَمَّا فُرِضَ شَهْرُ رَمَضَانَ قالَ: مَن شَاءَ صَامَهُ وَمَن شَاءَ تَرَكَهُ.\n\n- الراوي:  عائشة أم المؤمنين \n- حكم الحديث: [صحيح]",
"عَنْ عَائِشَةَ رَضِيَ اللَّهُ عَنْهَا: أنَّ يَومَ عَاشُورَاءَ كانَ يُصَامُ في الجَاهِلِيَّةِ، فَلَمَّا جَاءَ الإسْلَامُ مَن شَاءَ صَامَهُ وَمَن شَاءَ تَرَكَهُ.\n\n- الراوي: عائشة أم المؤمنين \n- حكم الحديث: [صحيح]",
"أنَّ ابْنَ عُمَرَ، كانَ إذَا جَدَّ به السَّيْرُ جَمع بيْنَ المَغْرِبِ وَالْعِشَاءِ بَعْدَ أنَّ يَغِيبَ الشَّفَقُ، ويقولُ: إنَّ رَسولَ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ كانَ إذَا جَدَّ به السَّيْرُ جَمع بيْنَ المَغْرِبِ وَالْعِشَاءِ.\n\n- الراوي: عبدالله بن عمر\n- حكم الحديث: [صحيح]",
"رَأَيْتُ رَسولَ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ يَجْمَعُ بيْنَ المَغْرِبِ وَالْعِشَاءِ إذَا جَدَّ به السَّيْرُ.\n\n- الراوي: عبدالله بن عمر \n- حكم الحديث: [صحيح]",
"إنِّي لأَمْشِي مع عبدِ اللهِ بنِ مَسْعُودٍ بمِنًى، إذْ لَقِيَهُ عُثْمَانُ بنُ عَفَّانَ، فَقالَ: هَلُمَّ يا أَبَا عبدِ الرَّحْمَنِ، قالَ: فَاسْتَخْلَاهُ، فَلَمَّا رَأَى عبدُ اللهِ أَنْ ليسَتْ له حَاجَةٌ، قالَ: قالَ لِي: تَعَالَ يا عَلْقَمَةُ، قالَ: فَجِئْتُ، فَقالَ له عُثْمَانُ: أَلَا نُزَوِّجُكَ يا أَبَا عبدِ الرَّحْمَنِ جَارِيَةً بكْرًا، لَعَلَّهُ يَرْجِعُ إلَيْكَ مِن نَفْسِكَ ما كُنْتَ تَعْهَدُ؟ فَقالَ عبدُ اللهِ: لَئِنْ قُلْتَ ذَاكَ، فَذَكَرَ بمِثْلِ حَديثِ أَبِي مُعَاوِيَةَ.\n\n- الراوي:  عبدالله بن مسعود\n- حكم الحديث: [صحيح]",
"دَخَلْتُ أَنَا وَعَمِّي عَلْقَمَةُ، وَالأسْوَدُ علَى عبدِ اللهِ بنِ مَسْعُودٍ قالَ: وَأَنَا شَابٌّ يَومَئذٍ، فَذَكَرَ حَدِيثًا رُئِيتُ أنَّهُ حَدَّثَ به مِن أَجْلِي، قالَ: قالَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ... بمِثْلِ حَديثِ أَبِي مُعَاوِيَةَ.\nوَزَادَ قالَ: فَلَمْ أَلْبَثْ حتَّى تَزَوَّجْتُ. [وفي رواية]: دَخَلْنَا عليه وَأَنَا أَحْدَثُ القَوْمِ. بمِثْلِ حَديثِهِمْ. وَلَمْ يَذْكُرْ: فَلَمْ أَلْبَثْ حتَّى تَزَوَّجْتُ.\n\n- الراوي: عبدالله بن مسعود \n- حكم الحديث: [صحيح]",
"عَنْ أَنَسٍ، أنَّ نَفَرًا مِن أَصْحَابِ النبيِّ صَلَّى اللَّهُ عليه وَسَلَّمَ سَأَلُوا أَزْوَاجَ النبيِّ صَلَّى اللَّهُ عليه وَسَلَّمَ عن عَمَلِهِ في السِّرِّ؟ فَقالَ بَعْضُهُمْ: لا أَتَزَوَّجُ النِّسَاءَ، وَقالَ بَعْضُهُمْ: لا آكُلُ اللَّحْمَ، وَقالَ بَعْضُهُمْ: لا أَنَامُ علَى فِرَاشٍ، فَحَمِدَ اللَّهَ وَأَثْنَى عليه. فَقالَ: ما بَالُ أَقْوَامٍ قالوا كَذَا وَكَذَا؟ لَكِنِّي أُصَلِّي وَأَنَامُ، وَأَصُومُ وَأُفْطِرُ، وَأَتَزَوَّجُ النِّسَاءَ، فمَن رَغِبَ عن سُنَّتي فليسَ مِنِّي.\n\n- الراوي: أنس بن مالك\n- حكم الحديث: [صحيح]",
"يَا مَعْشَرَ الشَّبَابِ، مَنِ اسْتَطَاعَ مِنْكُمُ البَاءَةَ فَلْيَتَزَوَّجْ، فإنَّه أَغَضُّ لِلْبَصَرِ، وَأَحْصَنُ لِلْفَرْجِ، وَمَن لَمْ يَسْتَطِعْ فَعليه بالصَّوْمِ، فإنَّه له وِجَاءٌ.\n\n- الراوي: عبدالله بن مسعود \n- حكم الحديث: [صحيح]",
"حدَّثَني البَرَاءُ، وهو غَيْرُ كَذُوبٍ أنَّهُمْ كَانُوا يُصَلُّونَ خَلْفَ رَسولِ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ فَإِذَا رَفَعَ رَأْسَهُ مِنَ الرُّكُوعِ لَمْ أرَ أحَدًا يَحْنِي ظَهْرَهُ، حتَّى يَضَعَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ جَبْهَتَهُ علَى الأرْضِ، ثُمَّ يَخِرُّ مَن ورَاءَهُ سُجَّدًا.\n\n- الراوي: البراء بن عازب \n- حكم الحديث: [صحيح]",
"كُنَّا مع النبيِّ صَلَّى اللَّهُ عليه وسلَّمَ لا يَحْنُو أحَدٌ مِنَّا ظَهْرَهُ، حتَّى نَرَاهُ قدْ سَجَدَ.\nفَقالَ زُهَيْرٌ: حَدَّثَنَا سُفْيَانُ قالَ: حَدَّثَنَا الكُوفِيُّونَ: أبَانُ وغَيْرُهُ قالَ حتَّى نَرَاهُ يَسْجُدُ.\n\n- الراوي: البراء بن عازب \n- حكم الحديث: [صحيح]",
"حَدَّثَنَا البَرَاءُ: أنَّهُمْ كَانُوا يُصَلُّونَ مع رَسولِ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ، فَإِذَا رَكَعَ رَكَعُوا، وإذَا رَفَعَ رَأْسَهُ مِنَ الرُّكُوعِ فَقالَ: سَمِعَ اللَّهُ لِمَن حَمِدَهُ لَمْ نَزَلْ قِيَامًا، حتَّى نَرَاهُ قدْ وضَعَ وجْهَهُ في الأرْضِ ثُمَّ نَتَّبِعُهُ.\n\n- الراوي: البراء بن عازب\n- حكم الحديث: [صحيح]",
"حدَّثَني البَرَاءُ، وهو غَيْرُ كَذُوبٍ قالَ: كانَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ، إذَا قالَ: سَمِعَ اللهِ لِمَن حَمِدَهُ لَمْ يَحْنِ أحَدٌ مِنَّا ظَهْرَهُ، حتَّى يَقَعَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ سَاجِدًا، ثُمَّ نَقَعُ سُجُودًا بَعْدَهُ.\n\n- الراوي: البراء بن عازب\n- حكم الحديث: [صحيح]",
"[يمكن أن يضاف للحديث الذي قبله] وفي رواية: صَلَّى بنَا رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ وأَبُو بَكْرٍ خَلْفَهُ فَإِذَا كَبَّرَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ كَبَّرَ أبو بَكْرٍ لِيُسْمِعَنَا...، ثُمَّ ذَكَرَ نَحْوَ حَديثِ اللَّيْثِ.\n\n- الراوي: جابر بن عبدالله \n- حكم الحديث: [صحيح]",
"لَمَّا مَرِضَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ مَرَضَهُ الذي تُوُفِّيَ فِيهِ.\nوفي حَديثِ ابْنِ مُسْهِرٍ: فَأُتِيَ برَسولِ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ حتَّى أُجْلِسَ إلى جَنْبِهِ وكانَ النبيُّ صَلَّى اللَّهُ عليه وسلَّمَ يُصَلِّي بالنَّاسِ وأَبُو بَكْرٍ يُسْمِعُهُمُ التَّكْبِيرَ.\nوفي حَديثِ عِيسَى: فَجَلَسَ رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ يُصَلِّي وأَبُو بَكْرٍ إلى جَنْبِهِ وأَبُو بَكْرٍ يُسْمِعُ النَّاسَ.\n\n- الراوي: عائشة ام المؤمنين\n- حكم الحديث: [صحيح]",
"لا يُجْلَدُ أحَدٌ فَوْقَ عَشَرَةِ أسْواطٍ، إلَّا في حَدٍّ مِن حُدُودِ اللَّهِ.\n\n- الراوي: هانئ بن نيار أبو بردة \n- حكم الحديث: [صحيح]",
"مَن أَحَبَّ لِقَاءَ اللهِ، أَحَبَّ اللَّهُ لِقَاءَهُ، وَمَن كَرِهَ لِقَاءَ اللهِ، كَرِهَ اللَّهُ لِقَاءَهُ فَقُلتُ: يا نَبِيَّ اللهِ أَكَرَاهيةُ المَوْتِ؟ فَكُلُّنَا نَكْرَهُ المَوْتَ، فَقالَ: ليسَ كَذَلِكِ، وَلَكِنَّ المُؤْمِنَ إِذَا بُشِّرَ برَحْمَةِ اللهِ وَرِضْوَانِهِ وَجَنَّتِهِ، أَحَبَّ لِقَاءَ اللهِ، فأحَبَّ اللَّهُ لِقَاءَهُ، وإنَّ الكَافِرَ إِذَا بُشِّرَ بعَذَابِ اللهِ وَسَخَطِهِ، كَرِهَ لِقَاءَ اللهِ، وَكَرِهَ اللَّهُ لِقَاءَهُ.\n\n- الراوي: عائشة أم المؤمنين \n- حكم الحديث: [صحيح]",
"مَن أَحَبَّ لِقَاءَ اللهِ، أَحَبَّ اللَّهُ لِقَاءَهُ، وَمَن كَرِهَ لِقَاءَ اللهِ، كَرِهَ اللَّهُ لِقَاءَهُ، وَالْمَوْتُ قَبْلَ لِقَاءِ اللَّهِ.\n\n- الراوي: عائشة أم المؤمنين\n- حكم الحديث: [صحيح]",
"مَن أَحَبَّ لِقَاءَ اللهِ، أَحَبَّ اللَّهُ لِقَاءَهُ، وَمَن كَرِهَ لِقَاءَ اللهِ، كَرِهَ اللَّهُ لِقَاءَهُ.\n\n- الراوي: عبادة بن الصامت \n- حكم الحديث: [صحيح]",
"مَن أَحَبَّ لِقَاءَ اللهِ، أَحَبَّ اللَّهُ لِقَاءَهُ، وَمَن كَرِهَ لِقَاءَ اللهِ، كَرِهَ اللَّهُ لِقَاءَهُ.\n\n- الراوي: أبو موسى الأشعري \n- حكم الحديث: [صحيح]",
"مَن رَأَى مِن أمِيرِهِ شيئًا يَكْرَهُهُ فَلْيَصْبِرْ، فإنَّه مَن فارَقَ الجَماعَةَ شِبْرًا، فَماتَ، فَمِيتَةٌ جاهِلِيَّةٌ.\n\n- الراوي: عبدالله بن عباس \n- حكم الحديث: [صحيح]",
"مَن كَرِهَ مِن أمِيرِهِ شيئًا، فَلْيَصْبِرْ عليه، فإنَّه ليسَ أحَدٌ مِنَ النَّاسِ خَرَجَ مِنَ السُّلْطانِ شِبْرًا، فَماتَ عليه، إلَّا ماتَ مِيتَةً جاهِلِيَّةً.\n\n- الراوي: عبدالله بن عباس \n- حكم الحديث: [صحيح]",
"أنَّ عُمَرَ بنَ الخَطَّابِ جَاءَ إلى رَسولِ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ، وَعِنْدَهُ نِسْوَةٌ قدْ رَفَعْنَ أَصْوَاتَهُنَّ علَى رَسولِ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ، فَلَمَّا اسْتَأْذَنَ عُمَرُ ابْتَدَرْنَ الحِجَابَ.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"اجْتَمع نِسَاءُ النبيِّ صَلَّى اللَّهُ عليه وَسَلَّمَ، فَلَمْ يُغَادِرْ منهنَّ امْرَأَةً، فَجَاءَتْ فَاطِمَةُ تَمْشِي كَأنَّ مِشْيَتَهَا مِشْيَةُ رَسُولِ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ، فَقالَ: مَرْحَبًا بابْنَتي فأجْلَسَهَا عن يَمِينِهِ، أَوْ عن شِمَالِهِ، ثُمَّ إنَّه أَسَرَّ إلَيْهَا حَدِيثًا فَبَكَتْ فَاطِمَةُ، ثُمَّ إنَّه سَارَّهَا فَضَحِكَتْ أَيْضًا، فَقُلتُ لَهَا: ما يُبْكِيكِ؟ فَقالَتْ: ما كُنْتُ لِأُفْشِيَ سِرَّ رَسُولِ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ، فَقُلتُ: ما رَأَيْتُ كَالْيَومِ فَرَحًا أَقْرَبَ مِن حُزْنٍ، فَقُلتُ لَهَا حِينَ بَكَتْ: أَخَصَّكِ رَسُولُ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ بحَديثِهِ دُونَنَا، ثُمَّ تَبْكِينَ؟ وَسَأَلْتُهَا عَمَّا قالَ فَقالَتْ: ما كُنْتُ لِأُفْشِيَ سِرَّ رَسُولِ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ، حتَّى إذَا قُبِضَ سَأَلْتُهَا فَقالَتْ: إنَّه كانَ حدَّثَني أنَّ جِبْرِيلَ كانَ يُعَارِضُهُ بالقُرْآنِ كُلَّ عَامٍ مَرَّةً، وإنَّه عَارَضَهُ به في العَامِ مَرَّتَيْنِ، وَلَا أُرَانِي إلَّا قدْ حَضَرَ أَجَلِي، وإنَّكِ أَوَّلُ أَهْلِي لُحُوقًا بي، وَنِعْمَ السَّلَفُ أَنَا لَكِ، فَبَكَيْتُ لذلكَ، ثُمَّ إنَّه سَارَّنِي، فَقالَ: أَلَا تَرْضَيْنَ أَنْ تَكُونِي سَيِّدَةَ نِسَاءِ المُؤْمِنِينَ، أَوْ سَيِّدَةَ نِسَاءِ هذِه الأُمَّةِ فَضَحِكْتُ لذلكَ.\n\n- الراوي: عائشة ام المؤمنين \n- حكم الحديث: [صحيح]",
"أنَّ النبيَّ صَلَّى اللَّهُ عليه وَسَلَّمَ، خَطَبَ أُمَّ هَانِئٍ، بنْتَ أَبِي طَالِبٍ فَقالَتْ: يا رَسُولَ اللهِ، إنِّي قدْ كَبِرْتُ، وَلِي عِيَالٌ، فَقالَ رَسُولُ اللهِ صَلَّى اللَّهُ عليه وَسَلَّمَ: خَيْرُ نِسَاءٍ رَكِبْنَ...، ثُمَّ ذَكَرَ بمِثْلِ حَديثِ يُونُسَ غيرَ أنَّهُ قالَ أَحْنَاهُ علَى وَلَدٍ في صِغَرِهِ.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"خَيْرُ نِساءٍ رَكِبْنَ الإبِلَ، قالَ أحَدُهُما: صالِحُ نِساءِ قُرَيْشٍ، وقالَ الآخَرُ: نِساءُ قُرَيْشٍ، أحْناهُ علَى يَتِيمٍ في صِغَرِهِ، وأَرْعاهُ علَى زَوْجٍ في ذاتِ يَدِهِ.تَجِدُونَ مِن شَرِّ النَّاسِ ذا الوَجْهَيْنِ، الذي يَأْتي هَؤُلاءِ بوَجْهٍ، وهَؤُلاءِ بوَجْهٍ. بمِثْلِهِ، غيرَ أنَّه قالَ: أرْعاهُ علَى ولَدٍ في صِغَرِهِ ولَمْ يَقُلْ يَتِيمٍ.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"خَيْرُ نِساءٍ رَكِبْنَ الإبِلَ، صالِحُ نِساءِ قُرَيْشٍ أحْناهُ علَى ولَدٍ في صِغَرِهِ، وأَرْعاهُ علَى زَوْجٍ في ذاتِ يَدِهِ.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"نِساءُ قُرَيْشٍ خَيْرُ نِساءٍ رَكِبْنَ الإبِلَ، أحْناهُ علَى طِفْلٍ، وأَرْعاهُ علَى زَوْجٍ في ذاتِ يَدِهِ.\nقالَ: يقولُ أبو هُرَيْرَةَ علَى إثْرِ ذلكَ: ولَمْ تَرْكَبْ مَرْيَمُ بنْتُ عِمْرانَ بَعِيرًا قَطُّ.\n\n- الراوي: أبو هريرة\n- حكم الحديث: [صحيح]",
"سَأَلْتُ عَبْدَ اللهِ بنَ أَبِي أَوْفَى، هلْ أَوْصَى رَسولُ اللهِ صَلَّى اللَّهُ عليه وسلَّمَ، فَقالَ: لَا، قُلتُ: فَلِمَ كُتِبَ علَى المُسْلِمِينَ الوَصِيَّةُ؟ أَوْ فَلِمَ أُمِرُوا بالوَصِيَّةِ؟ قالَ: أَوْصَى بكِتَابِ اللهِ عَزَّ وَجَلَّ.[وفي رواية]: مِثْلَهُ.\nغَيْرَ أنَّ في حَديثِ وَكِيعٍ، قُلتُ: فَكيفَ أُمِرَ النَّاسُ بالوَصِيَّةِ؟\nوفي حَديثِ ابْنِ نُمَيْرٍ، قُلتُ: كيفَ كُتِبَ علَى المُسْلِمِينَ الوَصِيَّةُ؟\n\n- الراوي: عبدالله بن أبي أوفى \n- حكم الحديث: [صحيح]",
"صلي ع شفيعنا يوم القيامة"
]
